INSERT INTO `posts` (`id`, `author_id`, `category_id`, `title`, `seo_title`, `excerpt`, `body`, `image`, `slug`, `meta_description`, `meta_keywords`, `status`, `featured`, `created_at`, `updated_at`) VALUES
(29, 5, 12, 'Đẩy mạnh công tác tiêm vắc xin phòng COVID-19 trên địa bàn tỉnh', NULL, 'Ban Tuyên giáo Tỉnh ủy vừa ban hành Công văn số 1000-CV/BTGTU về việc đẩy mạnh tuyên truyền công tác tiêm vắc xin phòng COVID-19 trên địa bàn tỉnh.', '<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><strong style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Ban Tuy&ecirc;n gi&aacute;o Tỉnh ủy vừa ban h&agrave;nh C&ocirc;ng văn số 1000-CV/BTGTU về việc đẩy mạnh tuy&ecirc;n truyền c&ocirc;ng t&aacute;c ti&ecirc;m vắc xin ph&ograve;ng COVID-19 tr&ecirc;n địa b&agrave;n tỉnh.</span></span></strong></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Trong C&ocirc;ng văn nhấn mạnh, thời gian qua, dịch bệnh COVID-19 vẫn diễn biến phức tạp, kh&oacute; lường v&agrave; đ&atilde; b&ugrave;ng ph&aacute;t trở lại tại một số quốc gia ở Ch&acirc;u &Acirc;u v&agrave; Ch&acirc;u &Aacute;. Tại Việt Nam đ&atilde; ghi nhận sự xuất hiện của hai biến thể phụ trong cộng đồng BA.4, BA.5 của biến chủng Omicrom. Tuy nhi&ecirc;n, thời gian qua, nhiều người d&acirc;n khi đ&atilde; ti&ecirc;m vắc xin mũi cơ bản hoặc từng mắc COVID-19 bắt đầu chủ quan, lơ l&agrave; trong việc &aacute;p dụng c&aacute;c biện ph&aacute;p ph&ograve;ng chống bệnh. V&igrave; vậy, tỷ lệ người d&acirc;n tr&ecirc;n địa b&agrave;n tỉnh ti&ecirc;m mũi 3, mũi 4 v&agrave; ti&ecirc;m cho trẻ từ 05 đến 11 tuổi rất thấp, nằm trong nh&oacute;m tỷ lệ ti&ecirc;m vắc xin COVID-19 thấp nhất cả nước.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Thời gian tới, Ban Tuy&ecirc;n gi&aacute;o Tỉnh ủy đề&nbsp;nghị c&aacute;c địa phương, cơ quan, đơn vị căn cứ theo chức năng, nhiệm vụ của m&igrave;nh tuy&ecirc;n truyền với nhiều h&igrave;nh thức kh&aacute;c nhau, li&ecirc;n tục, thường xuy&ecirc;n, cung cấp đầy đủ th&ocirc;ng tin ch&iacute;nh x&aacute;c đến người d&acirc;n nhằm đẩy mạnh c&ocirc;ng t&aacute;c ti&ecirc;m vắc xin ph&ograve;ng COVID-19 tr&ecirc;n địa b&agrave;n tỉnh./.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\">&nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/Copy of cv-1000-btg.signed_page-0001.jpg\" alt=\"\" width=\"50%\" height=\"50%\" /></p>\r\n<p><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\"><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/Copy of cv-1000-btg.signed_page-0002.jpg\" alt=\"\" width=\"50%\" height=\"50%\" /><br /></span></span></span></span></p>\r\n<p style=\"text-align: center;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/Copy of cv-1000-btg.signed_page-0003.jpg\" alt=\"\" width=\"50%\" height=\"50%\" /></span></span></span></span></p>', 'posts/December2022/6MYhM5yASaTm6vFucRpx.jpg', 'day-manh-cong-tac-tiem-vac-xin-phong-covid-19-tren-dia-ban-tinh', 'Ban Tuyên giáo Tỉnh ủy vừa ban hành Công văn số 1000-CV/BTGTU về việc đẩy mạnh tuyên truyền công tác tiêm vắc xin phòng COVID-19 trên địa bàn tỉnh.', 'Ban Tuyên giáo Tỉnh ủy vừa ban hành Công văn số 1000-CV/BTGTU về việc đẩy mạnh tuyên truyền công tác tiêm vắc xin phòng COVID-19 trên địa bàn tỉnh.', 'PUBLISHED', 1, '2022-12-29 19:36:00', '2022-12-29 19:55:47'),
(30, 5, 12, 'Tăng cường công tác phòng, dịch đảm bảo hoạt động của ngành văn hóa, thể thao và du lịch', 'Tăng cường công tác phòng, dịch đảm bảo hoạt động của ngành văn hóa, thể thao và du lịch', 'Thực hiện Công điện số 01/CĐ-UBND tinh ngày 24/02/2022 của Uỷ ban nhân dân tỉnh về tăng cường công tác phòng, dịch đảm bảo hoạt động của cơ quan nhà nước; Công văn số 762/BYT-DP ngày 21/02/2022 của Bộ Y tế về cách ly y tế đối với ca bệnh COVID-19 và các trường hợp tiếp xúc gần,', '<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><strong style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Thực hiện C&ocirc;ng điện số 01/CĐ-UBND tinh ng&agrave;y 24/02/2022 của Uỷ ban nh&acirc;n d&acirc;n tỉnh về tăng cường c&ocirc;ng t&aacute;c ph&ograve;ng, dịch đảm bảo hoạt động của cơ quan nh&agrave; nước; C&ocirc;ng văn số 762/BYT-DP ng&agrave;y 21/02/2022 của Bộ Y tế về c&aacute;ch ly y tế đối với ca bệnh COVID-19 v&agrave; c&aacute;c trường hợp tiếp x&uacute;c gần, Sở Văn hóa, Thể thao v&agrave; Du lịch vừa ban h&agrave;nh C&ocirc;ng văn số 318/SVHTTDL-VP đề nghị đến c&aacute;c ph&ograve;ng chuy&ecirc;n m&ocirc;n, đơn vị sự nghiệp trực thuộc Sở; phòng Văn hóa v&agrave; Th&ocirc;ng tin các huyện, thị xã, th&agrave;nh phố; các đơn vị sự nghiệp VHTTDL các huyện, thị xã, th&agrave;nh phố; c&aacute;c hiệp hội, hội, li&ecirc;n đo&agrave;n, CLB văn hóa, thể thao, du lịch; c&aacute;c đơn vị kinh doanh dịch vụ văn hóa, thể thao, du lịch tăng cường c&ocirc;ng t&aacute;c ph&ograve;ng, dịch&nbsp; đảm bảo hoạt động của ng&agrave;nh văn h&oacute;a, thể thao v&agrave; du lịch.</span></span></strong></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Theo đ&oacute;, đề cao tinh thần tr&aacute;ch nhiệm trong chỉ đạo c&ocirc;ng t&aacute;c ph&ograve;ng, chống dịch tại đơn vị; &aacute;p dụng linh hoạt c&aacute;c biện ph&aacute;p ph&ograve;ng, chống dịch, đảm bảo m&ocirc;i trường l&agrave;m việc an to&agrave;n, hiệu quả, ph&ugrave; hợp với t&igrave;nh h&igrave;nh diễn biến phức tạp của dịch bệnh.Tăng cường h&igrave;nh thức họp trực tuyến, họp online; ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin trong giải quyết c&ocirc;ng việc, chỉ đạo, điều h&agrave;nh hoạt động tại đơn vị, nhằm hạn chế việc tiếp x&uacute;c trực tiếp tại đơn vị. Qu&aacute;n triệt c&aacute;n bộ, c&ocirc;ng chức, vi&ecirc;n chức, người lao động trong đơn vị n&ecirc;u cao tinh thần cảnh gi&aacute;c, tự gi&aacute;c trong ph&ograve;ng, chống dịch, đảm bảo an to&agrave;n đối với đơn vị, gia đ&igrave;nh, bản th&acirc;n v&agrave; cộng đồng; gương mẫu, đi đầu trong việc thực hiện c&aacute;c biện ph&aacute;p ph&ograve;ng, chống dịch đặc biệt l&agrave; theo d&otilde;i sức khoẻ v&agrave; thực hiện nghi&ecirc;m nguy&ecirc;n tắc 5K; đeo khẩu trang trong suốt qu&aacute; tr&igrave;nh l&agrave;m việc v&agrave; tiếp x&uacute;c với người kh&aacute;c, kh&ocirc;ng v&igrave; đ&atilde; ti&ecirc;m vắc-xin m&agrave; chủ quan trong ph&ograve;ng, chống dịch; đồng thời tuy&ecirc;n truyền, vận động gia đ&igrave;nh, người th&acirc;n, bạn b&egrave;, du kh&aacute;ch tăng cường &yacute; thức tự gi&aacute;c, tr&aacute;ch nhiệm trong việc thực hiện c&aacute;c biện ph&aacute;p ph&ograve;ng, chống dịch. Đối với c&aacute;c trường hợp nghi nhiễm hoặc c&oacute; tiếp x&uacute;c với F0 tại đơn vị th&igrave; cần b&igrave;nh tỉnh, th&ocirc;ng b&aacute;o với L&atilde;nh đạo đơn vị v&agrave; chủ động phối hợp thực hiện c&aacute;c biện ph&aacute;p xử l&yacute;, theo d&otilde;i sức khoẻ theo đ&uacute;ng hướng dẫn của cơ quan, đơn vị, chức năng. L&atilde;nh đạo c&aacute;c đơn vị chịu tr&aacute;ch nhiệm chỉ đạo, điều h&agrave;nh đơn vị vừa thực hiện tốt c&ocirc;ng t&aacute;c ph&ograve;ng chống dịch, vừa đảm bảo tiến độ thực hiện nhiệm vụ c&ocirc;ng t&aacute;c theo kế hoạch v&agrave; hoạt động kinh doanh tại đơn vị.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Đồng thời, chủ động thực hiện c&aacute;ch ly y tế đối với c&aacute;c trường hợp tiếp x&uacute;c gần theo C&ocirc;ng văn số 762/BYT-DP ng&agrave;y 21/02/2022 của Bộ Y tế về c&aacute;ch ly y tế đối với ca bệnh COVID-19 v&agrave; c&aacute;c trường hợp tiếp x&uacute;c gần./.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: right;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><strong style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">BBT</span></span></strong></span></span></p>', 'posts/December2022/6ovK3DNCPhh0MJAKvVay.jpg', 'tang-cuong-cong-tac-phong-dich-dam-bao-hoat-dong-cua-nganh-van-hoa-the-thao-va-du-lich', 'Thực hiện Công điện số 01/CĐ-UBND tinh ngày 24/02/2022 của Uỷ ban nhân dân tỉnh về tăng cường công tác phòng, dịch đảm bảo hoạt động của cơ quan nhà nước; Công văn số 762/BYT-DP ngày 21/02/2022 của Bộ Y tế về cách ly y tế đối với ca bệnh COVID-19 và các trường hợp tiếp xúc gần,', 'Thực hiện Công điện số 01/CĐ-UBND tinh ngày 24/02/2022 của Uỷ ban nhân dân tỉnh về tăng cường công tác phòng, dịch đảm bảo hoạt động của cơ quan nhà nước; Công văn số 762/BYT-DP ngày 21/02/2022 của Bộ Y tế về cách ly y tế đối với ca bệnh COVID-19 và các trường hợp tiếp xúc gần,', 'PUBLISHED', 0, '2022-12-29 19:38:06', '2022-12-29 19:57:19'),
(32, 5, 12, 'Tăng cường công tác phòng, chống dịch đảm bảo hoạt động của cơ quan nhà nước', 'Tăng cường công tác phòng, chống dịch đảm bảo hoạt động của cơ quan nhà nước', 'Chiều 24/02, Chủ tịch UBND tỉnh Phạm Ngọc Nghị đã có Công điện số 01/CĐ-UBND gửi Thủ trưởng các sở, ban, ngành trong tỉnh; Chủ tịch UBND các huyện, thị xã, thành phố về tăng cường công tác phòng chống dịch, bảo đảm hoạt động của cơ quan Nhà nước.', '<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><strong style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Chiều 24/02, Chủ tịch UBND tỉnh Phạm Ngọc Nghị đ&atilde; c&oacute; C&ocirc;ng điện số 01/CĐ-UBND gửi Thủ trưởng c&aacute;c sở, ban, ng&agrave;nh trong tỉnh; Chủ tịch UBND c&aacute;c huyện, thị x&atilde;, th&agrave;nh phố về tăng cường c&ocirc;ng t&aacute;c ph&ograve;ng chống dịch, bảo đảm hoạt động của cơ quan Nh&agrave; nước.</span></span></strong></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">C&ocirc;ng điện n&ecirc;u r&otilde;, từ sau dịp nghỉ Tết Nguy&ecirc;n đ&aacute;n Nh&acirc;m Dần 2022, t&igrave;nh h&igrave;nh dịch COVID-19 tr&ecirc;n địa b&agrave;n tỉnh b&ugrave;ng ph&aacute;t mạnh với số ca nhiễm li&ecirc;n tục tăng cao. Đ&aacute;ng lưu &yacute;, số ca mắc trong c&aacute;n bộ l&atilde;nh đạo, c&ocirc;ng chức, vi&ecirc;n chức, người lao động tại c&aacute;c cơ quan Nh&agrave; nước ng&agrave;y c&agrave;ng nhiều, ảnh hưởng kh&ocirc;ng tốt đến c&ocirc;ng t&aacute;c v&agrave; hoạt động chỉ đạo, điều h&agrave;nh của cơ quan, đơn vị.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Để tăng cường hiệu quả c&ocirc;ng t&aacute;c ph&ograve;ng chống dịch, đảm bảo hoạt động c&ocirc;ng t&aacute;c của cơ quan, đơn vị v&agrave; chỉ đạo điều h&agrave;nh của tỉnh, Chủ tịch UBND tỉnh y&ecirc;u cầu Thủ trưởng c&aacute;c sở, ban, ng&agrave;nh; Chủ tịch UBND c&aacute;c huyện, thị x&atilde;, th&agrave;nh phố đề cao tinh thần tr&aacute;ch nhiệm trong chỉ đạo c&ocirc;ng t&aacute;c ph&ograve;ng chống dịch tại cơ quan, đơn vị, địa phương; &aacute;p dụng linh hoạt c&aacute;c biện ph&aacute;p ph&ograve;ng chống dịch, đảm bảo m&ocirc;i trường l&agrave;m việc an to&agrave;n, hiệu quả, ph&ugrave; hợp với t&igrave;nh h&igrave;nh diễn biến phức tạp của dịch bệnh. Tăng cường h&igrave;nh thức họp trực tuyến, ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin trong giải quyết c&ocirc;ng việc, chỉ đạo, điều h&agrave;nh hoạt động, nhằm hạn chế việc tiếp x&uacute;c trực tiếp tại cơ quan, đơn vị.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Đồng thời qu&aacute;n triệt c&aacute;n bộ, c&ocirc;ng chức, vi&ecirc;n chức, người lao động trong cơ quan, đơn vị, n&ecirc;u cao cảnh gi&aacute;c, tự gi&aacute;c trong ph&ograve;ng chống dịch, đảm bảo an to&agrave;n đối với cơ quan, gia đ&igrave;nh v&agrave; bản th&acirc;n; gương mẫu đi đầu trong việc thực hiện c&aacute;c biện ph&aacute;p ph&ograve;ng, chống dịch, đặc biệt l&agrave; tự theo d&otilde;i sức khỏe v&agrave; thực hiện nguy&ecirc;n tắc 5K; đeo khẩu trang trong suốt qu&aacute; tr&igrave;nh l&agrave;m việc v&agrave; tiếp x&uacute;c với người kh&aacute;c, kh&ocirc;ng tụ tập, đến nơi đ&ocirc;ng người khi kh&ocirc;ng cần thiết; kh&ocirc;ng v&igrave; đ&atilde; ti&ecirc;m vắc xin m&agrave; chủ quan trong ph&ograve;ng chống dịch; tuy&ecirc;n truyền, vận động gia đ&igrave;nh, người th&acirc;n, bạn b&egrave; tăng cường &yacute; thức tự gi&aacute;c, tr&aacute;ch nhiệm trong việc thực hiện c&aacute;c biện ph&aacute;p ph&ograve;ng chống dịch.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Thủ trưởng c&aacute;c sở, ban, ng&agrave;nh; Chủ tịch UBND c&aacute;c huyện, thị x&atilde;, th&agrave;nh phố chịu tr&aacute;ch nhiệm chỉ đạo, điều h&agrave;nh cơ quan, đơn vị, địa phương vừa thực hiện tốt c&ocirc;ng t&aacute;c ph&ograve;ng chống dịch, vừa đảm bảo tiến độ thực hiện nhiệm vụ c&ocirc;ng t&aacute;c theo kế hoạch, kh&ocirc;ng để chậm trễ, đ&igrave;nh trệ&nbsp; c&ocirc;ng việc của cơ quan, đơn vị, địa phương.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Đối với c&aacute;c trường hợp nghi nhiễm hoặc c&oacute; tiếp x&uacute;c với F0, Chủ tịch UBND tỉnh đề nghị cần b&igrave;nh tĩnh, th&ocirc;ng b&aacute;o với cơ quan, đơn vị, địa phương v&agrave; chủ động phối hợp thực hiện c&aacute;c biện ph&aacute;p xử l&yacute;, theo d&otilde;i sức khỏe theo đ&uacute;ng hướng dẫn của cơ quan, đơn vị chức năng./.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: right;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><strong style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">BBT</span></span></strong></span></span></p>', 'posts/December2022/yxi1mweNj9beLnsePJ70.jpg', 'tang-cuong-cong-tac-phong-chong-dich-dam-bao-hoat-dong-cua-co-quan-nha-nuoc', 'Chiều 24/02, Chủ tịch UBND tỉnh Phạm Ngọc Nghị đã có Công điện số 01/CĐ-UBND gửi Thủ trưởng các sở, ban, ngành trong tỉnh; Chủ tịch UBND các huyện, thị xã, thành phố về tăng cường công tác phòng chống dịch, bảo đảm hoạt động của cơ quan Nhà nước.', 'Chiều 24/02, Chủ tịch UBND tỉnh Phạm Ngọc Nghị đã có Công điện số 01/CĐ-UBND gửi Thủ trưởng các sở, ban, ngành trong tỉnh; Chủ tịch UBND các huyện, thị xã, thành phố về tăng cường công tác phòng chống dịch, bảo đảm hoạt động của cơ quan Nhà nước.', 'PUBLISHED', 0, '2022-12-29 19:41:39', '2022-12-29 19:57:11'),
(33, 7, 31, 'TP. Buôn Ma Thuột: Từ 6 giờ ngày 23/2, tạm dừng hoạt động cơ sở kinh doanh các dịch vụ có nguy cơ lây nhiễm cao', 'TP. Buôn Ma Thuột: Từ 6 giờ ngày 23/2, tạm dừng hoạt động cơ sở kinh doanh các dịch vụ có nguy cơ lây nhiễm cao', 'Chiều 22/2, UBND TP.Buôn Ma Thuột đã có thông báo số 524/TB-UBND về việc áp dụng một số biện pháp phòng chống dịch COVID-19 trên địa bàn thành phố kể từ 6 giờ ngày 23/2/2022 cho đến khi có thông báo m', '<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><strong style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Chiều 22/2, UBND TP.Bu&ocirc;n Ma Thuột đ&atilde; c&oacute; th&ocirc;ng b&aacute;o số 524/TB-UBND về việc &aacute;p dụng một số biện ph&aacute;p ph&ograve;ng chống dịch COVID-19 tr&ecirc;n địa b&agrave;n th&agrave;nh phố kể từ 6 giờ ng&agrave;y 23/2/2022 cho đến khi c&oacute; th&ocirc;ng b&aacute;o mới.</span></span></strong></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Theo đ&oacute;, trong những ng&agrave;y qua, dịch COVID-19 tr&ecirc;n địa b&agrave;n TP. Bu&ocirc;n Ma Thuột đang diễn biến phức tạp, c&oacute; chiều hướng ng&agrave;y c&agrave;ng gia tăng, trung b&igrave;nh mỗi ng&agrave;y ghi nhận hơn 500 ca mắc mới. Để th&iacute;ch ứng an to&agrave;n, linh hoạt v&agrave; kiểm so&aacute;t hiệu quả dịch COVID-19 tr&ecirc;n địa b&agrave;n, UBND th&agrave;nh phố th&ocirc;ng b&aacute;o khẩn &aacute;p dụng một số biện ph&aacute;p ph&ograve;ng, chống dịch COVID-19.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Cụ thể, tổ chức hoạt động ngo&agrave;i trời, trong nh&agrave; (hội họp, tập huấn, hội thảo, sự kiện...) kh&ocirc;ng tập trung qu&aacute; 20 người tại c&ugrave;ng một thời điểm; tập trung dưới 50 người với điều kiện c&oacute; tr&ecirc;n 100% người tham gia được ti&ecirc;m đủ 2 mũi vắc xin hoặc khỏi bệnh COVID-19 kh&ocirc;ng qu&aacute; 6 th&aacute;ng.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Ri&ecirc;ng tổ chức đ&aacute;m tang phải c&oacute; sự gi&aacute;m s&aacute;t của ch&iacute;nh quyền địa phương. Hoạt động vận tải h&agrave;nh kh&aacute;ch tuyến cố định nội th&agrave;nh v&agrave; li&ecirc;n tỉnh, xe bu&yacute;t, taxi, hợp đồng, du lịch được hoạt động nhưng phải thực hiện nguy&ecirc;n tắc 5K, nhất l&agrave; khai b&aacute;o y tế, s&aacute;t khuẩn, đ&atilde; ti&ecirc;m đủ 2 mũi vắc xin; kiểm so&aacute;t chặt chẽ tại bến xe v&agrave; h&agrave;nh kh&aacute;ch tr&ecirc;n xe.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">C&aacute;c hoạt động kinh doanh dịch vụ (trung t&acirc;m thương mại, si&ecirc;u thị, cửa h&agrave;ng tiện &iacute;ch, chợ đầu mối, chợ truyền thống,...) hoạt động với điều kiện người tham gia đ&atilde; ti&ecirc;m đủ 2 mũi vắc xin hoặc khỏi bệnh COVID-19 kh&ocirc;ng qu&aacute; 6 th&aacute;ng, thực hiện nghi&ecirc;m việc đeo khẩu trang, s&aacute;t khuẩn, giữ khoảng c&aacute;ch an to&agrave;n.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">C&aacute;c cơ sở kinh doanh dịch vụ ăn uống được hoạt động khi người tham gia đ&atilde; ti&ecirc;m đủ 2 mũi vắc xin hoặc khỏi bệnh COVID-19 kh&ocirc;ng qu&aacute; 6 th&aacute;ng, phục vụ kh&ocirc;ng qu&aacute; 20 người trong c&ugrave;ng một thời điểm, giữ khoảng c&aacute;ch c&aacute;c b&agrave;n 2 m&eacute;t; khuyến kh&iacute;ch kh&ocirc;ng phục vụ trực tiếp m&agrave; chuyển sang h&igrave;nh thức b&aacute;n mang về hoặc giao h&agrave;ng tại nh&agrave;. Ri&ecirc;ng tổ chức đ&aacute;m cưới kh&ocirc;ng qu&aacute; 50 người. Hoạt động b&aacute;n h&agrave;ng rong, v&eacute; số dạo... được hoạt động với điều kiện: người b&aacute;n phải được ti&ecirc;m đủ 2 mũi vắc xin hoặc khỏi COVID-19 kh&ocirc;ng qu&aacute; 6 th&aacute;ng.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Tạm dừng hoạt động cơ sở kinh doanh c&aacute;c dịch vụ c&oacute; nguy cơ l&acirc;y nhiễm cao (vũ trường, karaoke, m&aacute;txa, qu&aacute;n bar, Internet, tr&ograve; chơi điện tử), c&aacute;c cơ sở l&agrave;m đẹp gồm l&agrave;m m&oacute;ng, trang điểm, spa... Ri&ecirc;ng cắt t&oacute;c hoạt động c&oacute; điều kiện: người kinh doanh v&agrave; người tham gia đ&atilde; ti&ecirc;m đủ 2 mũi vắc xin hoặc khỏi bệnh COVID-19 kh&ocirc;ng qu&aacute; 6 th&aacute;ng; phục vụ kh&ocirc;ng qu&aacute; 30% c&ocirc;ng suất trong c&ugrave;ng một thời điểm; bảo đảm c&aacute;c biện ph&aacute;p ph&ograve;ng, chống dịch hiệu quả.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Tạm dừng c&aacute;c hoạt động gi&aacute;o dục đối với bậc mầm non v&agrave; triển khai hoạt động gi&aacute;o dục trực tuyến đối với bậc tiểu học, lớp 6, 7, 8 v&agrave; lớp 10, 11. Ri&ecirc;ng lớp 9, lớp 12 v&agrave; c&aacute;c cơ sở gi&aacute;o dục v&agrave; đ&agrave;o tạo kh&aacute;c tr&ecirc;n địa b&agrave;n th&agrave;nh phố tổ chức dạy học trực tiếp nhưng c&oacute; điều kiện: đảm bảo an to&agrave;n trong c&ocirc;ng t&aacute;c ph&ograve;ng, chống dịch COVID-19; giảng vi&ecirc;n, sinh vi&ecirc;n, học sinh tham gia dạy v&agrave; học phải được ti&ecirc;m đủ 2 mũi vắc xin hoặc khỏi bệnh COVID-19 kh&ocirc;ng qu&aacute; 6 th&aacute;ng.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">C&aacute;c cơ sở t&ocirc;n gi&aacute;o, t&iacute;n ngưỡng thờ tự được hoạt động c&oacute; điều kiện: người tham gia phải ti&ecirc;m đủ 2 mũi vắc xin hoặc khỏi bệnh COVID-19 kh&ocirc;ng qu&aacute; 06 th&aacute;ng; hoạt động kh&ocirc;ng qu&aacute; 20 người; thực hiện đảm bảo nguy&ecirc;n tắc 5K. Hoạt động lữ h&agrave;nh, tham quan tại khu du lịch, điểm du lịch, dịch vụ ăn uống trong cơ sở kinh doanh du lịch, cơ sở lưu tr&uacute; (kh&aacute;ch sạn, nh&agrave; nghỉ...) được hoạt động c&oacute; điều kiện: người tham gia phải được ti&ecirc;m đủ 2 mũi vắc xin hoặc khỏi COVID-19 kh&ocirc;ng qu&aacute; 6 th&aacute;ng, phục vụ kh&ocirc;ng qu&aacute; 50% c&ocirc;ng suất.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Thư viện, bảo t&agrave;ng, rạp chiếu phim, cơ sở, địa điểm biểu diễn văn h&oacute;a, nghệ thuật, tập luyện thể dục, thể thao ngo&agrave;i trời v&agrave; trong nh&agrave; được hoạt động c&oacute; điều kiện: Người tham gia đ&atilde; được ti&ecirc;m đủ 2 mũi vắc xin hoặc khỏi COVID-19 kh&ocirc;ng qu&aacute; 6 th&aacute;ng; thực hiện đảm bảo nguy&ecirc;n tắc 5K, qu&eacute;t m&atilde; QR; phục vụ kh&ocirc;ng qu&aacute; 30% c&ocirc;ng suất.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">C&aacute;c cơ sở sản xuất, đơn vị thi c&ocirc;ng c&aacute;c c&ocirc;ng tr&igrave;nh x&acirc;y dựng được hoạt động c&oacute; điều kiện: c&aacute;n bộ quản l&yacute;, c&ocirc;ng nh&acirc;n phải được ti&ecirc;m đủ 2 mũi vắc xin hoặc khỏi COVID-19 kh&ocirc;ng qu&aacute; 6 th&aacute;ng; thực hiện bảo đảm nguy&ecirc;n tắc 5K, qu&eacute;t m&atilde; QR; c&oacute; phương &aacute;n, kế hoạch ph&ograve;ng, chống dịch COVID-19 chủ động, an to&agrave;n v&agrave; hiệu quả. Đồng thời, định kỳ x&eacute;t nghiệm cho người c&oacute; nguy cơ bằng phương ph&aacute;p x&eacute;t nghiệm nhanh kh&aacute;ng nguy&ecirc;n hoặc PCR cho 5-10% số c&aacute;n bộ, c&ocirc;ng nh&acirc;n đang tham gia hoạt động sản xuất, thi c&ocirc;ng./.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: right;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><strong style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Nguồn: https://baodaklak.vn/</span></span></strong></span></span></p>', 'posts/December2022/qwY4PMAccyF1yFu1XZyW.jpg', 'tp-buon-ma-thuot-tu-6-gio-ngay-23-2-tam-dung-hoat-dong-co-so-kinh-doanh-cac-dich-vu-co-nguy-co-lay-nhiem-cao', 'Chiều 22/2, UBND TP.Buôn Ma Thuột đã có thông báo số 524/TB-UBND về việc áp dụng một số biện pháp phòng chống dịch COVID-19 trên địa bàn thành phố kể từ 6 giờ ngày 23/2/2022 cho đến khi có thông báo m', 'Chiều 22/2, UBND TP.Buôn Ma Thuột đã có thông báo số 524/TB-UBND về việc áp dụng một số biện pháp phòng chống dịch COVID-19 trên địa bàn thành phố kể từ 6 giờ ngày 23/2/2022 cho đến khi có thông báo m', 'PUBLISHED', 1, '2022-12-29 19:43:07', '2023-01-13 00:31:16'),
(45, 7, 29, 'Kỷ niệm 50 năm Chiến thắng Hà Nội - Điện Biên Phủ trên không (18/12/1972 - 18/12/2022)', NULL, NULL, '<p><span style=\"color: #222222; font-family: \'Roboto Condensed\', sans-serif;\">Kỷ niệm 50 năm Chiến thắng H&agrave; Nội - Điện Bi&ecirc;n Phủ tr&ecirc;n kh&ocirc;ng (18/12/1972 - 18/12/2022).</span></p>\r\n<p><span style=\"color: #222222; font-family: \'Roboto Condensed\', sans-serif;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/1234_20221106212431.jpg\" alt=\"\" width=\"100%\" /></span></p>\r\n<p><span style=\"color: #222222; font-family: \'Roboto Condensed\', sans-serif;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/z3963741355122_d62f33125fad3320fcdce6b964796f4e.jpg\" alt=\"\" width=\"100%\" /></span></p>\r\n<p><span style=\"color: #222222; font-family: \'Roboto Condensed\', sans-serif;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/z3963741355179_36ac8fa4570d63b0013fda5aed403607.jpg\" alt=\"\" width=\"100%\" /></span></p>', 'posts/December2022/agzkEliiPkod1dovTegm.jpg', 'ky-niem-50-nam-chien-thang-ha-noi-dien-bien-phu-tren-khong-18-12-1972-18-12-2022', NULL, NULL, 'PUBLISHED', 0, '2022-12-29 20:23:26', '2023-01-13 00:39:24'),
(46, 5, 19, 'Chào mừng kỷ niệm 78 năm Ngày thành lập Quân đội Nhân dân Việt Nam (22/12/1994-22/12/2022) và 33 năm Ngày hội Quốc phòng toàn dân (22/12/1989-22/12/2022)', NULL, NULL, '<h1 class=\"heading-wrp\" style=\"box-sizing: border-box; font-size: 18px; margin: 0px 0px 10px; font-family: \'Roboto Condensed\', sans-serif; line-height: 27px; color: #182045;\"><span style=\"color: #222222; font-size: 14px; font-weight: 400;\">Ch&agrave;o mừng kỷ niệm 78 năm Ng&agrave;y th&agrave;nh lập Qu&acirc;n đội Nh&acirc;n d&acirc;n Việt Nam (22/12/1994-22/12/2022) v&agrave; 33 năm Ng&agrave;y hội Quốc ph&ograve;ng to&agrave;n d&acirc;n (22/12/1989-22/12/2022)</span></h1>\r\n<p><span style=\"color: #222222; font-size: 14px; font-weight: 400;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/22-12 (4).jpg\" alt=\"\" width=\"100%\" /></span></p>\r\n<p><span style=\"color: #222222; font-size: 14px; font-weight: 400;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/22-12 (3).jpg\" alt=\"\" width=\"100%\" /></span></p>\r\n<p><span style=\"color: #222222; font-size: 14px; font-weight: 400;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/22-12 (1).jpg\" alt=\"\" width=\"100%\" /></span></p>', 'posts/December2022/heXFD9OExYIOLVmkXvjn.jpg', 'chao-mung-ky-niem-78-nam-ngay-thanh-lap-quan-doi-nhan-dan-viet-nam-22-12-1994-22-12-2022-va-33-nam-ngay-hoi-quoc-phong-toan-dan-22-12-1989-22-12-2022', NULL, NULL, 'PUBLISHED', 0, '2022-12-29 20:25:20', '2022-12-29 20:25:20'),
(47, 5, 17, 'Nhiệt liệt chào mừng 132 năm ngày sinh Chủ tịch Hồ Chí Minh vĩ đại (19/5/1890 - 19/5/2022)', NULL, NULL, '<p>Nhiệt liệt ch&agrave;o mừng 132 năm ng&agrave;y sinh Chủ tịch Hồ Ch&iacute; Minh vĩ đại (19/5/1890 - 19/5/2022)</p>\r\n<p><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/ngay sinh CT Ho Chi Minh (1).jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/ngay sinh CT Ho Chi Minh (2).jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/ngay sinh CT Ho Chi Minh (3).jpg\" alt=\"\" width=\"100%\" /></p>', 'posts/December2022/qqtg7ckN8w7PoyXH3AaP.jpg', 'nhiet-liet-chao-mung-132-nam-ngay-sinh-chu-tich-ho-chi-minh-v-dai-19-5-1890-19-5-2022', NULL, NULL, 'PUBLISHED', 0, '2022-12-29 20:26:34', '2023-01-10 00:51:53'),
(48, 5, 17, 'Mẫu tranh tuyên truyền chào mừng 68 năm Ngày chiến thắng Điện Biên Phủ (7/5/1954 - 7/5/2022)', NULL, NULL, '<p>Mẫu tranh tuy&ecirc;n truyền ch&agrave;o mừng 68 năm Ng&agrave;y chiến thắng Điện Bi&ecirc;n Phủ (7/5/1954 - 7/5/2022)</p>\r\n<p><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/Dien Bien Phu.jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/z3963741355122_d62f33125fad3320fcdce6b964796f4e1.jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/Dien bien phu 1.jpg\" alt=\"\" width=\"100%\" /></p>', 'posts/December2022/ODqVKTJsS5MdXaQT8Do1.jpg', 'mau-tranh-tuyen-truyen-chao-mung-68-nam-ngay-chien-thang-dien-bien-phu-7-5-1954-7-5-2022', NULL, NULL, 'PUBLISHED', 0, '2022-12-29 20:28:27', '2023-01-10 00:52:11'),
(49, 5, 20, 'Triển lãm Câu lạc bộ Mỹ thuật \"Sắc màu tháng 10\"', NULL, 'Sáng 20/10, Câu lạc bộ Mỹ thuật thuộc Trung tâm Văn hóa tỉnh Đắk Lắk tổ chức Triển lãm Mỹ thuật nhóm nữ họa sĩ “Sắc màu tháng 10” lần thứ 4 năm 2017. Các họa sĩ có tác phẩm tham gia: Trần Hồng Lâm, Bùi Thị Nam, Trần Thị Đào, Nguyễn Thị Dung, Phí Lê Thương, Thu Vân, Trà My, Dương Vy Hà, Thu Hiền, Phạm Thị Hòa, Lê Hồng, Thùy Giang, Võ Hiếu, Trần Thị Duy.', '<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif;\"><span style=\"box-sizing: border-box; font-family: Arial;\"><span style=\"box-sizing: border-box; font-size: larger;\"><span style=\"box-sizing: border-box; text-align: justify; text-indent: 36pt;\">S&aacute;ng 20/10, C&acirc;u lạc bộ Mỹ thuật&nbsp;</span><span style=\"box-sizing: border-box; text-align: justify; text-indent: 36pt;\">thuộc Trung t&acirc;m Văn h&oacute;a tỉnh Đắk Lắk</span><span style=\"box-sizing: border-box; text-align: justify; text-indent: 36pt;\">&nbsp;tổ chức Triển l&atilde;m Mỹ thuật nh&oacute;m nữ họa sĩ &ldquo;Sắc m&agrave;u th&aacute;ng 10&rdquo; lần thứ 4 năm 2017. C&aacute;c họa sĩ c&oacute; t&aacute;c phẩm tham gia: Trần Hồng L&acirc;m, B&ugrave;i Thị Nam, Trần Thị Đ&agrave;o, Nguyễn Thị Dung, Ph&iacute; L&ecirc; Thương, Thu V&acirc;n, Tr&agrave; My, Dương Vy H&agrave;, Thu Hiền, Phạm Thị H&ograve;a, L&ecirc; Hồng, Th&ugrave;y Giang, V&otilde; Hiếu, Trần Thị Duy.</span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><span style=\"box-sizing: border-box; font-family: Arial;\"><span style=\"box-sizing: border-box; font-size: larger;\"><span style=\"box-sizing: border-box; text-align: justify; text-indent: 36pt;\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%;\" src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/IMG_0545.jpg\" alt=\"\" width=\"600\" height=\"353\" /></span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-indent: 36pt; text-align: center;\"><em style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt; font-family: \'Times New Roman\', serif; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"box-sizing: border-box; font-family: Arial;\"><span style=\"box-sizing: border-box; font-size: smaller;\">&Ocirc;ng B&ugrave;i Văn Khối, Gi&aacute;m đốc Trung t&acirc;m Văn h&oacute;a tặng hoa cho c&aacute;c nữ họa sĩ</span></span></span></em></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 36pt;\"><span style=\"box-sizing: border-box; font-family: Arial;\"><span style=\"box-sizing: border-box; font-size: larger;\">&Yacute; nghĩa lớn nhất của triển l&atilde;m lần n&agrave;y l&agrave; tạo sự gắn kết, giao lưu, học tập lẫn nhau giữa c&aacute;c nữ họa sĩ, động vi&ecirc;n tinh thần, thắp l&ecirc;n ngọn lửa đam m&ecirc; s&aacute;ng tạo nghệ thuật.&nbsp;</span></span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center; text-indent: 36pt;\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%;\" src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/khan gia xem trien lam 3.jpg\" alt=\"\" width=\"600\" height=\"450\" /></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center; text-indent: 36pt;\"><span style=\"box-sizing: border-box; font-family: Arial;\"><span style=\"box-sizing: border-box; font-size: smaller;\">Một g&oacute;c triển l&atilde;m</span></span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 36pt;\">&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 36pt;\"><span style=\"box-sizing: border-box; font-family: Arial;\"><span style=\"box-sizing: border-box; font-size: larger;\">Hầu hết c&aacute;c t&aacute;c phẩm tập trung phản &aacute;nh ch&acirc;n thật cuộc sống, hướng đến c&aacute;i đẹp, cố gắng t&igrave;m t&ograve;i, s&aacute;ng tạo c&aacute;i mới, g&oacute;p phần gợi l&ecirc;n sinh kh&iacute; hoạt động nghệ thuật của tỉnh nh&agrave;. Mỗi t&aacute;c phẩm nghệ thuật ẩn chứa t&acirc;m tư, kh&aacute;t vọng, những cảm x&uacute;c trước sự đổi mới, ph&aacute;t triển từng ng&agrave;y của qu&ecirc; hương, hướng về c&aacute;i đẹp v&agrave; t&igrave;nh y&ecirc;u của người phụ nữ.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 36pt;\"><span style=\"box-sizing: border-box; font-family: Arial;\"><span style=\"box-sizing: border-box; font-size: larger;\">Triển l&atilde;m mở cửa từ ng&agrave;y 20-10 đến 25-10-2017 tại Trung t&acirc;m Văn h&oacute;a tỉnh Đắk Lắk.</span></span></p>\r\n<p><span style=\"box-sizing: border-box; font-family: Arial;\"><span style=\"box-sizing: border-box; font-size: larger;\">&nbsp;</span></span></p>', 'posts/December2022/QQ1tLfZo1mRwOAFA2m4p.jpg', 'trien-lam-cau-lac-bo-my-thuat-sac-mau-thang-10', 'Sáng 20/10, Câu lạc bộ Mỹ thuật thuộc Trung tâm Văn hóa tỉnh Đắk Lắk tổ chức Triển lãm Mỹ thuật nhóm nữ họa sĩ “Sắc màu tháng 10” lần thứ 4 năm 2017. Các họa sĩ có tác phẩm tham gia: Trần Hồng Lâm, Bùi Thị Nam, Trần Thị Đào, Nguyễn Thị Dung, Phí Lê Thương, Thu Vân, Trà My, Dương Vy Hà, Thu Hiền, Phạm Thị Hòa, Lê Hồng, Thùy Giang, Võ Hiếu, Trần Thị Duy.', 'Sáng 20/10, Câu lạc bộ Mỹ thuật thuộc Trung tâm Văn hóa tỉnh Đắk Lắk tổ chức Triển lãm Mỹ thuật nhóm nữ họa sĩ “Sắc màu tháng 10” lần thứ 4 năm 2017. Các họa sĩ có tác phẩm tham gia: Trần Hồng Lâm, Bùi Thị Nam, Trần Thị Đào, Nguyễn Thị Dung, Phí Lê Thương, Thu Vân, Trà My, Dương Vy Hà, Thu Hiền, Phạm Thị Hòa, Lê Hồng, Thùy Giang, Võ Hiếu, Trần Thị Duy.', 'PUBLISHED', 0, '2022-12-29 20:31:29', '2022-12-29 20:31:29'),
(50, 5, 20, 'Triển lãm mỹ thuật “Sắc màu tháng 10” lần thứ 2 năm 2015', NULL, 'Kỷ niệm 85 năm ngày thành lập Hội Liên hiệp Phụ nữ Việt Nam (20-10-1930 – 20-10-2015), Câu lạc bộ Mỹ thuật (Trung tâm Văn hóa tỉnh) tổ chức Triển lãm mỹ thuật “Sắc màu tháng 10” lần thứ 2 năm 2015. Triển lãm năm nay thu hút được 27 tác phẩm của 14 tác giả nữ là hội viên Câu lạc bộ đang làm việc và sinh sống trên địa bàn tỉnh', '<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif;\"><span style=\"box-sizing: border-box; font-family: Arial;\"><span style=\"box-sizing: border-box; font-size: larger;\"><span style=\"box-sizing: border-box; text-align: justify; text-indent: 0.5in;\">Kỷ niệm 85 năm ng&agrave;y th&agrave;nh lập Hội Li&ecirc;n hiệp Phụ nữ Việt Nam (20-10-1930 &ndash; 20-10-2015), C&acirc;u lạc bộ Mỹ thuật (Trung t&acirc;m Văn h&oacute;a tỉnh) tổ chức Triển l&atilde;m mỹ thuật &ldquo;Sắc m&agrave;u th&aacute;ng 10&rdquo; lần thứ 2 năm 2015.</span><span style=\"box-sizing: border-box; text-align: justify; text-indent: 0.5in;\">&nbsp;Triển l&atilde;m năm nay thu h&uacute;t được 27 t&aacute;c phẩm của 14 t&aacute;c giả nữ l&agrave; hội vi&ecirc;n C&acirc;u lạc bộ đang l&agrave;m việc v&agrave; sinh sống tr&ecirc;n địa b&agrave;n tỉnh. C&aacute;c t&aacute;c phẩm tham gia triển l&atilde;m được thể hiện bằng nhiều chất liệu kh&aacute;c nhau như: sơn m&agrave;i, sơn dầu, khắc gỗ, đồ họa&hellip; với chủ đề tự do.</span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/_DSC1029.jpg\" alt=\"\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><span style=\"box-sizing: border-box; font-family: Arial;\"><span style=\"box-sizing: border-box; font-size: larger;\"><span style=\"box-sizing: border-box; text-align: justify; text-indent: 0.5in;\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%;\" src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/_DSC1034.jpg\" alt=\"\" width=\"500\" height=\"332\" /></span></span></span></p>', 'posts/December2022/rt7kOdsgDP3X6tfSC4R2.jpg', 'trien-lam-my-thuat-sac-mau-thang-10-lan-thu-2-nam-2015', 'Kỷ niệm 85 năm ngày thành lập Hội Liên hiệp Phụ nữ Việt Nam (20-10-1930 – 20-10-2015), Câu lạc bộ Mỹ thuật (Trung tâm Văn hóa tỉnh) tổ chức Triển lãm mỹ thuật “Sắc màu tháng 10” lần thứ 2 năm 2015. Triển lãm năm nay thu hút được 27 tác phẩm của 14 tác giả nữ là hội viên Câu lạc bộ đang làm việc và sinh sống trên địa bàn tỉnh', 'Kỷ niệm 85 năm ngày thành lập Hội Liên hiệp Phụ nữ Việt Nam (20-10-1930 – 20-10-2015), Câu lạc bộ Mỹ thuật (Trung tâm Văn hóa tỉnh) tổ chức Triển lãm mỹ thuật “Sắc màu tháng 10” lần thứ 2 năm 2015. Triển lãm năm nay thu hút được 27 tác phẩm của 14 tác giả nữ là hội viên Câu lạc bộ đang làm việc và sinh sống trên địa bàn tỉnh', 'PUBLISHED', 0, '2022-12-29 20:32:59', '2022-12-29 20:32:59');
INSERT INTO `posts` (`id`, `author_id`, `category_id`, `title`, `seo_title`, `excerpt`, `body`, `image`, `slug`, `meta_description`, `meta_keywords`, `status`, `featured`, `created_at`, `updated_at`) VALUES
(51, 5, 20, 'TRIỂN LÃM MỸ THUẬT KHU VỰC NAM MIỀN TRUNG - TÂY NGUYÊN LẦN THỨ XIX', NULL, 'Hội Mỹ thuật Việt Nam vừa phố hợp với Sở VHTTDL và Hội Văn học Nghệ thuật tỉnh Kon Tum tổ chức Triển lãm Mỹ thuật Khu vực Nam Miền trung và Tây Nguyên lần thứ XIX,  chào mừng kỷ niệm 69 năm Cách mạng Tháng Tám và Quốc khánh nước Cộng hòa xã hội chủ nghĩa Việt Nam (2/9/1945 – 2/9/2014).', '<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif;\"><span style=\"box-sizing: border-box; font-family: Tahoma;\"><span style=\"box-sizing: border-box; font-size: small;\"><span style=\"box-sizing: border-box; text-align: justify; text-indent: 0.5in;\">Hội Mỹ thuật Việt Nam vừa phố hợp với Sở VHTTDL v&agrave; Hội Văn học Nghệ thuật tỉnh Kon Tum tổ chức Triển l&atilde;m Mỹ thuật Khu vực Nam Miền trung v&agrave; T&acirc;y Nguy&ecirc;n lần thứ XIX,&nbsp;</span><span style=\"box-sizing: border-box; text-align: justify; text-indent: 0.5in;\">&nbsp;ch&agrave;o mừng kỷ niệm 69 năm C&aacute;ch mạng Th&aacute;ng T&aacute;m v&agrave; Quốc kh&aacute;nh nước Cộng h&ograve;a x&atilde; hội chủ nghĩa Việt Nam (2/9/1945 &ndash; 2/9/2014).</span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-align: justify;\"><span style=\"box-sizing: border-box; font-family: Tahoma;\"><span style=\"box-sizing: border-box; font-size: small;\">&nbsp; &nbsp; &nbsp; Triển l&atilde;m gi&oacute;i thiệu 187 t&aacute;c phẩm của 184 t&aacute;c giả, gồm c&aacute;c thể loại: Đồ họa, Mỹ thuật ứng dụng, Đi&ecirc;u khắc. Trong đ&oacute;, c&oacute; 56 t&aacute;c giả l&agrave; Hội vi&ecirc;n hội Mỹ thuật Việt Nam v&agrave; 128 Hội vi&ecirc;n Hội Mỹ thuật thuộc c&aacute;c tỉnh: Kon Tum. Gia Lai, Đắk Lắk, Quảng Nam, Đ&agrave; Nẵng, Quảng Ng&atilde;i, B&igrave;nh Định, Ph&uacute; Y&ecirc;n, Kh&aacute;nh H&ograve;a</span></span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: small; font-family: Tahoma;\">&nbsp; &nbsp; &nbsp; Tỉnh Đắk Lắk c&oacute; 13 t&aacute;c giả với 14 t&aacute;c phẩm tham gia triển l&atilde;m.&nbsp; T&aacute;c phẩm &ldquo;Nắng v&agrave; Gi&oacute;&rdquo; của t&aacute;c giả Nguyễn T&acirc;n đoạt giải B.&nbsp;</span></p>\r\n<p><span style=\"box-sizing: border-box; font-size: small; font-family: Tahoma;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-align: center;\"><span style=\"box-sizing: border-box; font-size: small; font-family: Tahoma;\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%;\" src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/gui di copy.jpg\" alt=\"\" width=\"500\" height=\"364\" /></span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-align: center;\">Tranh cổ động của &nbsp;Phạm Xu&acirc;n Quang (Đắk Lắk) tham gia triển l&atilde;m</p>\r\n<p>&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-align: right; text-indent: 0.5in;\" align=\"right\"><span style=\"box-sizing: border-box; font-family: Tahoma;\"><span style=\"box-sizing: border-box; font-size: small;\"><strong style=\"box-sizing: border-box;\">Ama Phong</strong></span></span></p>', 'posts/December2022/EjwTQcIUkdEBGvqhjeqd.jpg', 'trien-lam-my-thuat-khu-vuc-nam-mien-trung-tay-nguyen-lan-thu-xix', 'Hội Mỹ thuật Việt Nam vừa phố hợp với Sở VHTTDL và Hội Văn học Nghệ thuật tỉnh Kon Tum tổ chức Triển lãm Mỹ thuật Khu vực Nam Miền trung và Tây Nguyên lần thứ XIX,  chào mừng kỷ niệm 69 năm Cách mạng Tháng Tám và Quốc khánh nước Cộng hòa xã hội chủ nghĩa Việt Nam (2/9/1945 – 2/9/2014).', 'Hội Mỹ thuật Việt Nam vừa phố hợp với Sở VHTTDL và Hội Văn học Nghệ thuật tỉnh Kon Tum tổ chức Triển lãm Mỹ thuật Khu vực Nam Miền trung và Tây Nguyên lần thứ XIX,  chào mừng kỷ niệm 69 năm Cách mạng Tháng Tám và Quốc khánh nước Cộng hòa xã hội chủ nghĩa Việt Nam (2/9/1945 – 2/9/2014).', 'PUBLISHED', 0, '2022-12-29 20:34:06', '2022-12-29 20:34:06'),
(52, 5, 20, 'ĐÔI ĐIỀU VỚI TRIỂN LÃM MỸ THUẬT KHU VỰC', NULL, '“Đến hẹn lại lên”, mỗi năm một lần các nghệ sĩ tạo hình trong các khu vực lại có dịp công bố những tác phẩm mới nhất của mình đến với công chúng yêu nghệ thuật và đồng nghiệp. Đây cũng là cơ hội để các nghệ sĩ gặp gỡ, giao lưu trao đổi học thuật, kinh nghiệm sáng tác và nâng cao chuyên môn nghề nghiệp.', '<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-family: Tahoma;\"><span style=\"box-sizing: border-box; font-size: larger;\">&nbsp;<span style=\"box-sizing: border-box; text-indent: 0.5in;\">&ldquo;Đến hẹn lại l&ecirc;n&rdquo;, mỗi năm một lần c&aacute;c nghệ sĩ tạo h&igrave;nh trong c&aacute;c khu vực lại c&oacute; dịp c&ocirc;ng bố những t&aacute;c phẩm mới nhất của m&igrave;nh đến với c&ocirc;ng ch&uacute;ng y&ecirc;u nghệ thuật v&agrave; đồng nghiệp. Đ&acirc;y cũng l&agrave; cơ hội để c&aacute;c nghệ sĩ gặp gỡ, giao lưu trao đổi học thuật, kinh nghiệm s&aacute;ng t&aacute;c v&agrave; n&acirc;ng cao chuy&ecirc;n m&ocirc;n nghề nghiệp.</span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 7.5pt 0in; color: #222222; font-family: \'Roboto Condensed\', sans-serif; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-align: justify; text-indent: 0.5in;\"><span style=\"box-sizing: border-box; font-family: Tahoma;\"><span style=\"box-sizing: border-box; font-size: larger;\">Triển l&atilde;m Mỹ thuật khu vực từ l&acirc;u đ&atilde; được khẳng định l&agrave; một s&acirc;n chơi lớn cho c&aacute;c nghệ sĩ tạo h&igrave;nh chuy&ecirc;n nghiệp trong c&aacute;c khu vực. Đ&acirc;y cũng l&agrave; nơi nu&ocirc;i dưỡng, ph&aacute;t hiện những t&agrave;i năng trẻ v&agrave; kh&ocirc;ng &iacute;t nghệ sĩ trẻ đ&atilde; bước v&agrave;o con đường s&aacute;ng tạo nghệ thuật v&agrave; khẳng định được gi&aacute; trị của m&igrave;nh tr&ecirc;n b&igrave;nh diện nghệ thuật quốc gia v&agrave; quốc tế. Triển l&atilde;m mỹ thuật khu vực như một &ldquo;bữa đại tiệc&rdquo; thịnh soạn những &ldquo;m&oacute;n ngon, vật lạ&rdquo; với những đặc trưng văn h&oacute;a v&ugrave;ng miền được c&aacute;c nghệ sĩ khắp c&aacute;c tỉnh th&agrave;nh trong khu vực mang đến cho c&ocirc;ng ch&uacute;ng y&ecirc;u nghệ thuật thưởng ngoạn. Ngo&agrave;i những nghệ sĩ đ&atilde; th&agrave;nh danh v&agrave; đ&atilde; định h&igrave;nh được phong c&aacute;ch s&aacute;ng t&aacute;c cho ri&ecirc;ng m&igrave;nh th&igrave; những nghệ sĩ trẻ lại mang đến triển l&atilde;m sự t&aacute;o bạo, hiện đại, với những &yacute; tưởng mới lạ pha trộn với sự m&atilde;nh liệt, quyết&nbsp; đo&aacute;n, d&aacute;m nghĩ, d&aacute;m l&agrave;m của những người trẻ. Họ mạnh dạn thay đổi c&aacute;ch nghĩ, c&aacute;ch nh&igrave;n về những gi&aacute; trị trong cuộc sống đương&nbsp; đại. Họ đặt vấn đề một c&aacute;ch thẳng thắn, trực diện để phản &aacute;nh ch&acirc;n thực&nbsp; những vấn đề nhức nhối thường nhật, do vậy t&iacute;nh thời sự v&agrave; nh&acirc;n bản l&agrave; điều ta dễ d&agrave;ng nhận thấy trong từng t&aacute;c phẩm. Ngo&agrave;i ra, một số nghệ sĩ từ l&acirc;u tưởng rằng đ&atilde; &ldquo;đ&oacute;ng khung&rdquo; &yacute; tưởng v&agrave; phong c&aacute;ch cũng đ&atilde; kh&ocirc;ng ngại ng&ugrave;ng thay đổi &ldquo;c&aacute;i t&ocirc;i nghệ thuật&rdquo; của m&igrave;nh để tr&igrave;nh l&agrave;ng những t&aacute;c phẩm lạ lẫm, tươi mới nhưng đủ sức thuyết phục bất kỳ&nbsp; &ldquo;kh&aacute;ch h&agrave;ng&rdquo; kh&oacute; t&iacute;nh n&agrave;o.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 7.5pt 0in; color: #222222; font-family: \'Roboto Condensed\', sans-serif; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-align: center; text-indent: 0.5in;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/DSC05801.jpg\" alt=\"\" width=\"80%\" /></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 7.5pt 0in; color: #222222; font-family: \'Roboto Condensed\', sans-serif; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-align: center; text-indent: 0.5in;\">Triển l&atilde;m Mỹ thuật \"M&ugrave;a thu Cao nguy&ecirc;n\" do Trung t&acirc;m Văn h&oacute;a tỉnh tổ chức định kỳ h&agrave;ng năm</p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 7.5pt 0in; color: #222222; font-family: \'Roboto Condensed\', sans-serif; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-align: justify; text-indent: 0.5in;\">&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 7.5pt 0in; color: #222222; font-family: \'Roboto Condensed\', sans-serif; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-align: justify; text-indent: 0.5in;\"><span style=\"box-sizing: border-box; font-family: Tahoma;\"><span style=\"box-sizing: border-box; font-size: larger;\">Trong bối cảnh Mỹ thuật Việt Nam c&oacute; rất nhiều s&acirc;n chơi với nhiều cấp độ, nhiều lứa tuổi, đa dạng h&igrave;nh thức với nhiều nguồn t&agrave;i trợ từ trong nước đến quốc tế, nhiều nghệ sĩ trẻ v&agrave; những nghệ sĩ đ&atilde; th&agrave;nh danh qua nhiều k&ecirc;nh th&ocirc;ng tin đ&atilde; c&oacute; kh&aacute; nhiều cơ hội c&ocirc;ng bố t&aacute;c phẩm của m&igrave;nh th&igrave; việc duy tr&igrave; triển l&atilde;m khu vực một c&aacute;ch ho&agrave;nh tr&aacute;ng v&agrave; quy m&ocirc; với nguồn kinh ph&iacute; c&oacute; phần eo hẹp của Nh&agrave; nước th&igrave; đ&atilde; l&agrave; một sự cố gắng nhất định của Hội Mỹ thuật Việt Nam. Nhưng ch&uacute;ng ta cũng kh&ocirc;ng thể kh&ocirc;ng nhận ra được một điều đ&oacute; l&agrave; tại c&aacute;c cuộc triển l&atilde;m mỹ thuật khu vực đ&atilde; xuất hiện sự cũ kỹ, lặp lại ch&iacute;nh m&igrave;nh, đa số nghệ sĩ thiếu đi sự cố gắng thay đổi phong c&aacute;ch s&aacute;ng t&aacute;c, tư duy, khuynh hướng nghệ thuật mới lạ bởi sự e ngại với ch&iacute;nh khả năng s&aacute;ng t&aacute;c của m&igrave;nh. Một số nghệ sĩ trẻ mới ra trường th&igrave; bị ảnh hưởng bởi những &ldquo;chiếc b&oacute;ng&rdquo; qu&aacute; lớn từ những người thầy, những lớp đ&agrave;n anh đ&atilde; th&agrave;nh danh ph&iacute;a trước, điều n&agrave;y đ&atilde; ảnh hưởng r&otilde; n&eacute;t tr&ecirc;n kh&ocirc;ng &iacute;t t&aacute;c phẩm tại triển l&atilde;m. Cuối c&ugrave;ng ta chỉ thấy một ph&ograve;ng tranh &ldquo;đ&egrave;m đẹp&rdquo;, xem được theo kiểu &ldquo;so b&oacute; đũa chọn cột cờ&rdquo;. C&oacute; những nghệ sĩ th&igrave; ngẫm ngợi rồi &ldquo;tự sướng&rdquo; với những giải thưởng đ&atilde; đạt được theo kiểu &ldquo;ăn m&agrave;y dĩ v&atilde;ng&rdquo; mang đến triển l&atilde;m rồi tự cho m&igrave;nh c&aacute;i quyền &ldquo;ph&aacute;n x&eacute;t&rdquo; đồng nghiệp, ch&ecirc; bai hội đồng nghệ thuật v&agrave; tự cho m&igrave;nh l&agrave; &ldquo;duy ng&atilde; độc t&ocirc;n&rdquo;. Lại c&oacute; những nghệ sĩ bằng những mối quan hệ c&aacute; nh&acirc;n rồi c&oacute; dăm ba lần xuất ngoại, khi trở về với triển l&atilde;m khu vực mang theo một &aacute;nh nh&igrave;n &ldquo;dưa hấu l&agrave;ng ta&hellip;&rdquo;, lợi dụng sự &iacute;t hiểu biết của một số người để ph&ocirc; b&agrave;y c&aacute;c chứng nhận, giải thưởng c&oacute; t&iacute;nh địa phương, phong tr&agrave;o như \"bằng chứng quốc tế\" về t&agrave;i năng. Qua b&aacute;o ch&iacute;, c&ocirc;ng ch&uacute;ng biết về c&aacute;c giải thưởng t&ecirc;n gọi rất &ldquo;vĩ m&ocirc;&rdquo; được một số nghệ sĩ trưng ra v&agrave; kh&ocirc;ng biết đ&oacute; chỉ l&agrave; giải thưởng của cuộc thi kh&ocirc;ng chuy&ecirc;n, giải thưởng do một tổ chức phi lợi nhuận n&agrave;o đ&oacute; tổ chức, &iacute;t c&oacute; gi&aacute; trị nghệ thuật. Vẫn biết c&aacute;c cuộc thi kh&ocirc;ng chuy&ecirc;n nghiệp, b&aacute;n chuy&ecirc;n nghiệp l&agrave; cơ hội để người c&oacute; năng khiếu nghệ thuật thể hiện, cũng l&agrave; nguồn quan trọng để ph&aacute;t hiện t&agrave;i năng bổ sung v&agrave;o đội ngũ nghệ sĩ chuy&ecirc;n nghiệp, nhưng kh&ocirc;ng phải c&aacute; nh&acirc;n c&oacute; t&agrave;i năng n&agrave;o cũng c&oacute; thể vượt qua khoảng c&aacute;ch giữa chuy&ecirc;n nghiệp với kh&ocirc;ng chuy&ecirc;n nghiệp v&agrave; b&aacute;n chuy&ecirc;n nghiệp bởi từ ph&aacute;t hiện t&agrave;i năng đến khi được c&ocirc;ng nhận l&agrave; một c&acirc;u chuyện d&agrave;i. Lại c&oacute; người kh&ocirc;ng t&igrave;m được nơi n&agrave;o ở nước ngo&agrave;i trao bằng khen, giấy x&aacute;c nhận, ngay lập tức bỏ kh&ocirc;ng tham gia bất kỳ cuộc triển l&atilde;m n&agrave;o&nbsp; tại Việt Nam. Những trường hợp vừa n&ecirc;u tr&ecirc;n kh&ocirc;ng phải l&agrave; hiếm, ta c&oacute; thể gặp bất kỳ ở đ&acirc;u, bất kỳ khu vực n&agrave;o v&agrave; kh&ocirc;ng chỉ&nbsp; g&oacute;i gọn trong một kh&ocirc;ng gian nhỏ hẹp.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 7.5pt 0in; color: #222222; font-family: \'Roboto Condensed\', sans-serif; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-align: justify; text-indent: 0.5in;\">&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 7.5pt 0in; color: #222222; font-family: \'Roboto Condensed\', sans-serif; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-align: justify; text-indent: 0.5in;\"><span style=\"box-sizing: border-box; font-family: Tahoma;\"><span style=\"box-sizing: border-box; font-size: larger;\">Trong c&aacute;c cuộc triển l&atilde;m khu vực, Hội đồng nghệ thuật lắm l&uacute;c cũng trao giải theo kiểu &ldquo;mặt trận&rdquo;, phong tr&agrave;o; trao giải theo kiểu ph&acirc;n bổ v&ugrave;ng miền để &ldquo;tất cả c&ugrave;ng vui vẻ&rdquo;. Việc trao giải của hội đồng nghệ thuật nhiều l&uacute;c cũng dấy l&ecirc;n sự bất phục trong suy nghĩ của đ&ocirc;ng đảo anh em nghệ sĩ t&acirc;m huyết v&agrave; y&ecirc;u nghề. Điều dễ d&agrave;ng nhận thấy l&agrave; hội đồng nghệ thuật thường xuy&ecirc;n cổ vũ, động vi&ecirc;n những khuynh hướng s&aacute;ng t&aacute;c mới v&agrave; hiện đại, tr&aacute;nh xa những &ldquo;lối m&ograve;n&rdquo; trong s&aacute;ng t&aacute;c. Tuy nhi&ecirc;n khi hội đồng nghệ thuật trao giải th&igrave; lại l&agrave; những t&aacute;c phẩm s&aacute;o m&ograve;n, &ldquo;cổ k&iacute;nh&rdquo;. Điều n&agrave;y đ&atilde; l&agrave;m cho c&aacute;c nghệ sĩ băn khoăn kh&ocirc;ng biết mục ti&ecirc;u v&agrave; định hướng của hội đồng nghệ thuật l&agrave; như thế n&agrave;o v&agrave; c&ocirc;ng ch&uacute;ng y&ecirc;u nghệ thuật th&igrave; mặc định rằng đ&oacute; l&agrave; những t&aacute;c phẩm xuất sắc nhất.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 7.5pt 0in; color: #222222; font-family: \'Roboto Condensed\', sans-serif; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-align: justify; text-indent: 0.5in;\"><span style=\"box-sizing: border-box; font-family: Tahoma;\"><span style=\"box-sizing: border-box; font-size: larger;\">Ngẫm cho c&ugrave;ng th&igrave; tất cả cũng l&agrave; s&acirc;n chơi, m&agrave; t&agrave;i năng th&igrave; kh&ocirc;ng phải ai cũng c&oacute; thể đạt đến sự cảnh giới v&ocirc; thượng để m&agrave; ph&oacute;ng b&uacute;t l&agrave; c&oacute; t&aacute;c phẩm để đời. Trong v&ograve;ng xo&aacute;y mi&ecirc;n man của hơi thở đương đại, c&aacute;c nghệ sĩ vẫn c&ograve;n c&oacute; thể cầm cọ để tạo ra t&aacute;c phẩm để g&oacute;p phần l&agrave;m đẹp cho đời cũng đ&atilde; l&agrave; một điều đ&aacute;ng qu&yacute; v&agrave; đ&aacute;ng tr&acirc;n trọng.</span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: right;\"><strong style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt; line-height: 21.4667px; font-family: \'Times New Roman\', \'serif\';\">Huy Lộc</span></strong></p>', 'posts/December2022/sOYCvKQi0pPDwQBXYUUV.jpg', 'doi-dieu-voi-trien-lam-my-thuat-khu-vuc', '“Đến hẹn lại lên”, mỗi năm một lần các nghệ sĩ tạo hình trong các khu vực lại có dịp công bố những tác phẩm mới nhất của mình đến với công chúng yêu nghệ thuật và đồng nghiệp. Đây cũng là cơ hội để các nghệ sĩ gặp gỡ, giao lưu trao đổi học thuật, kinh nghiệm sáng tác và nâng cao chuyên môn nghề nghiệp.', '“Đến hẹn lại lên”, mỗi năm một lần các nghệ sĩ tạo hình trong các khu vực lại có dịp công bố những tác phẩm mới nhất của mình đến với công chúng yêu nghệ thuật và đồng nghiệp. Đây cũng là cơ hội để các nghệ sĩ gặp gỡ, giao lưu trao đổi học thuật, kinh nghiệm sáng tác và nâng cao chuyên môn nghề nghiệp.', 'PUBLISHED', 0, '2022-12-29 20:36:33', '2022-12-29 20:36:33'),
(53, 5, 21, 'HỘI NGHỊ CÂU LẠC BỘ NHIẾP ẢNH BAN MÊ LẦN THỨ NHẤT NHIỆM KỲ 2014 - 2016', NULL, 'Câu lạc bộ nhiếp ảnh Ban Mê (thuộc Trung tân Văn hóa tỉnh) vừa tổ chức Hội nghị lần thứ nhất nhiệm kỳ 2014 – 2016, nhằm đánh giá hoạt động nhiệm kỳ lâm thời và đề ra phương hướng hoạt động nhiệm kỳ 2014 2016', '<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-family: Tahoma;\"><span style=\"box-sizing: border-box; font-size: larger;\"><span style=\"box-sizing: border-box; text-indent: 0.5in;\">C&acirc;u lạc bộ nhiếp ảnh Ban M&ecirc; (thuộc Trung t&acirc;n Văn h&oacute;a tỉnh) vừa tổ chức Hội nghị lần thứ nhất nhiệm kỳ 2014 &ndash; 2016, nhằm đ&aacute;nh gi&aacute; hoạt động nhiệm kỳ l&acirc;m thời v&agrave; đề ra phương hướng hoạt động nhiệm kỳ 2014 2016.</span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/10552382_284497478402102_4517651716085752422_n.jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\">Quang cảnh Hội nghị</p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 0.5in;\"><span style=\"box-sizing: border-box; font-family: Tahoma;\"><span style=\"box-sizing: border-box; font-size: larger;\">Được th&agrave;nh lập v&agrave;o giữa năm 2013,&nbsp;<span style=\"box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">dựa tr&ecirc;n cơ sở tự nguyện của c&aacute;c nghệ sỹ nhiếp ảnh chuy&ecirc;n v&agrave; kh&ocirc;ng chuy&ecirc;n, t&acirc;m huyết với phong tr&agrave;o nhiếp ảnh của địa phương nhằm giao lưu, trao đổi kỷ thuật v&agrave; kinh nghiệm s&aacute;ng t&aacute;c, đồng thời l&agrave; s&acirc;n chơi bổ &iacute;ch, l&agrave;nh mạnh của những người y&ecirc;u th&iacute;ch m&ocirc;n nghệ thuật nhiếp ảnh, sau gần 1 năm th&agrave;nh lập, đến nay CLB đ&atilde; thu h&uacute;t được tr&ecirc;n 30 tham gia.</span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 0.5in;\"><span style=\"box-sizing: border-box; font-family: Tahoma;\"><span style=\"box-sizing: border-box; font-size: larger;\">&nbsp;<span style=\"box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">Được sự quan t&acirc;m chỉ đạo của Ban gi&aacute;m đốc Trung t&acirc;m Văn h&oacute;a tỉnh, Ban chủ nhiệm đ&atilde; tổ chức&nbsp;</span>sinh hoạt cho c&acirc;u lạc bộ theo định kỳ, nội dung sinh hoạt phong ph&uacute;, đa dạng, như: tổ chức đi s&aacute;ng t&aacute;c nhiều điểm trong tỉnh. B&ecirc;n cạnh đ&oacute;, CLB c&ograve;n tổ chức giao lưu với nhiều CLB nhiếp ảnh ngo&agrave;i tỉnh, như: CLB nhiếp ảnh B&agrave; Rịa - Vũng T&agrave;u; CLB Nhiếp ảnh Hồng H&agrave; (H&agrave; Nội); CLB S&ocirc;ng H&agrave;n (Đ&agrave; Nẵng) v&agrave; Trại s&aacute;ng t&aacute;c &ldquo;Đại ng&agrave;n T&acirc;y Nguy&ecirc;n&rdquo; đ&atilde; tổ chức buổi giao lưu nh&acirc;n chuyến thăm v&agrave; s&aacute;ng t&aacute;c tại Đắk Lắk.&nbsp;</span></span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 0.5in;\"><span style=\"box-sizing: border-box; font-family: Tahoma;\"><span style=\"box-sizing: border-box; font-size: larger;\">&nbsp;Đặc biệt với sự t&agrave;i trợ kinh ph&iacute; của TTVH tỉnh C&acirc;u lạc bộ đ&atilde; Tổ chức&nbsp; triển l&atilde;m ảnh Thời sự Nghệ thuật với chủ đề &ldquo; Sắc xu&acirc;n Cao nguy&ecirc;n&rdquo; với &nbsp;60 t&aacute;c phẩm của c&aacute;c t&aacute;c giả l&agrave; hội vi&ecirc;n C&acirc;u lạc bộ, phản &aacute;nh cuộc sống đời thường, văn h&oacute;a, con người, phong cảnh; kh&ocirc;ng kh&iacute; đ&oacute;n năm mới 2014 v&agrave; Xu&acirc;n về tr&ecirc;n bi&ecirc;n giới&hellip; &nbsp;</span></span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 0.5in;\"><span style=\"box-sizing: border-box; font-family: Tahoma;\"><span style=\"box-sizing: border-box; font-size: larger;\">Tuy mới chỉ l&agrave; kết quả bước đầu, nhưng&nbsp;<span style=\"box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">CLB Nhiếp ảnh Ban M&ecirc; đang từng bước khẳng đinh m&igrave;nh, nhằm x&acirc;y dựng CLB ng&agrave;y c&agrave;ng vững mạnh, g&oacute;p phần đưa phong tr&agrave;o nghệ thuật nhiếp ảnh tỉnh nh&agrave; ng&agrave;y c&agrave;ng ph&aacute;t triển. Đồng thời, t</span>h&ocirc;ng qua h&igrave;nh ảnh g&oacute;p phần giới thiệu, quảng b&aacute; h&igrave;nh ảnh con người, văn h&oacute;a c&aacute;c d&acirc;n tộc T&acirc;y Nguy&ecirc;n đến với bạn b&egrave; trong v&agrave; ngo&agrave;i nước.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center; text-indent: 0.5in;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/10584015_284497508402099_7894232401614777933_n (1).jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 0.5in;\">&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center; text-indent: 0.5in;\"><span style=\"box-sizing: border-box; font-family: Tahoma;\"><span style=\"box-sizing: border-box; font-size: larger;\">Hội nghị đ&atilde; bầu ra 5 th&agrave;nh vi&ecirc;n v&agrave;o BCN nhiệm kỳ 2014 - 2016.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 0.5in;\"><span style=\"box-sizing: border-box; font-family: Tahoma;\"><span style=\"box-sizing: border-box; font-size: larger;\">Hội nghị đ&atilde; đề ra phương hướng v&agrave; nhiệm vụ trong nhiệm kỳ tới, đ&oacute; l&agrave;:&nbsp;<span style=\"box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">Thường xuy&ecirc;n tăng cường tổ chức giao lưu, s&aacute;ng t&aacute;c, trao đổi kinh nghiệm... &nbsp;Đồng thời ch&uacute; trọng ph&aacute;t triển hội vi&ecirc;n mới; Tạo t&igrave;nh đo&agrave;n kết gắn b&oacute; giữa c&aacute;c hội vi&ecirc;n trong CLB, hướng c&aacute;c hoạt động theo t&ocirc;n chỉ, mục đ&iacute;ch v&agrave; tu&acirc;n thủ c&aacute;c quy định của ph&aacute;p luật hiện h&agrave;nh, c&aacute;c quy định của tỉnh, của ng&agrave;nh về lĩnh vực Văn h&oacute;a&hellip;</span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 0.5in;\">&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: right;\"><strong style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Tahoma;\"><span style=\"box-sizing: border-box; font-size: larger;\">Tin, ảnh: Ho&agrave;ng Tuệ&nbsp;</span></span></strong></p>', 'posts/December2022/yPQ6kSdu4mwaFa2J25Y8.jpg', 'hoi-nghi-cau-lac-bo-nhiep-anh-ban-me-lan-thu-nhat-nhiem-ky-2014-2016', 'Câu lạc bộ nhiếp ảnh Ban Mê (thuộc Trung tân Văn hóa tỉnh) vừa tổ chức Hội nghị lần thứ nhất nhiệm kỳ 2014 – 2016, nhằm đánh giá hoạt động nhiệm kỳ lâm thời và đề ra phương hướng hoạt động nhiệm kỳ 2014 2016', 'Câu lạc bộ nhiếp ảnh Ban Mê (thuộc Trung tân Văn hóa tỉnh) vừa tổ chức Hội nghị lần thứ nhất nhiệm kỳ 2014 – 2016, nhằm đánh giá hoạt động nhiệm kỳ lâm thời và đề ra phương hướng hoạt động nhiệm kỳ 2014 2016', 'PUBLISHED', 0, '2022-12-29 20:38:35', '2022-12-29 20:38:35'),
(54, 5, 21, 'CÂU LẠC BỘ NHIẾP ẢNH BAN MÊ NƠI HỘI TỤ NHỮNG NIỀM ĐAM MÊ NHIẾP ẢNH NGHỆ THUẬT', NULL, NULL, '<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: larger;\"><span style=\"box-sizing: border-box; text-indent: 0.5in;\">Được th&agrave;nh lập v&agrave;o giữa năm 2013,&nbsp;<span style=\"box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">dựa tr&ecirc;n cơ sở tự nguyện của c&aacute;c nghệ sỹ nhiếp ảnh chuy&ecirc;n v&agrave; kh&ocirc;ng chuy&ecirc;n, t&acirc;m huyết với phong tr&agrave;o nhiếp ảnh của địa phương nhằm giao lưu, trao đổi kỷ thuật v&agrave; kinh nghiệm s&aacute;ng t&aacute;c, đồng thời l&agrave; s&acirc;n chơi bổ &iacute;ch, l&agrave;nh mạnh của những người y&ecirc;u th&iacute;ch m&ocirc;n nghệ thuật nhiếp ảnh. Ban đầu mới th&agrave;nh lập c&acirc;u lạc bộ thu h&uacute;t tr&ecirc;n 30 hội vi&ecirc;n v&agrave; đến nay, sau gần 1 năm th&agrave;nh lập. CLB đ&atilde; thu h&uacute;t được tr&ecirc;n 50 hội vi&ecirc;n v&agrave;</span></span><span style=\"box-sizing: border-box; text-indent: 0.5in;\">&nbsp;đ&atilde; bước đầu đi v&agrave;o ổn định.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 0.5in;\"><span style=\"box-sizing: border-box; font-size: larger;\">&nbsp;<span style=\"box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">Được sự quan t&acirc;m chỉ đạo của Ban gi&aacute;m đốc Trung t&acirc;m Văn h&oacute;a tỉnh, Ban chủ nhiệm đ&atilde; tổ chức&nbsp;</span>sinh hoạt cho c&acirc;u lạc bộ theo định kỳ, nội dung sinh hoạt phong ph&uacute;, đa dạng, như: tổ chức đi s&aacute;ng t&aacute;c tại Khu Du lịch bu&ocirc;n Ko Tam, Khu du lịch huyện Lắk v&agrave; Th&aacute;c Drai Nur v&agrave; c&aacute;c điểm trong tỉnh. Đặc biệt với sự t&agrave;i trợ kinh ph&iacute; của TTVH tỉnh C&acirc;u lạc bộ đ&atilde; Tổ chức&nbsp; triển l&atilde;m ảnh Thời sự Nghệ thuật với chủ đề &ldquo; Sắc xu&acirc;n Cao nguy&ecirc;n&rdquo; với &nbsp;60 t&aacute;c phẩm của c&aacute;c t&aacute;c giả l&agrave; hội vi&ecirc;n C&acirc;u lạc bộ, phản &aacute;nh cuộc sống đời thường, văn h&oacute;a, con người, phong cảnh; kh&ocirc;ng kh&iacute; đ&oacute;n năm mới 2014 v&agrave; Xu&acirc;n về tr&ecirc;n bi&ecirc;n giới&hellip; được c&aacute;c t&aacute;c giả thể hiện qua ống k&iacute;nh của m&igrave;nh một c&aacute;ch chỉn chu, ph&oacute;ng kho&aacute;ng mang đậm bản sắc văn h&oacute;a v&agrave; con người T&acirc;y Nguy&ecirc;n huyền thoại, nhưng kh&ocirc;ng k&eacute;m phần thơ mộng.</span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 0.5in;\"><span style=\"box-sizing: border-box; font-size: larger;\">B&ecirc;n cạnh đ&oacute;, CLB c&ograve;n tổ chức giao lưu với C&acirc;u lạc bộ nhiếp ảnh B&agrave; Rịa - Vũng T&agrave;u; C&acirc;u lạc bộ Nhiếp ảnh Hồng H&agrave; (H&agrave; Nội); CLB S&ocirc;ng H&agrave;n (Đ&agrave; Nẵng). Đặc biệt v&agrave; cuối th&aacute;ng 3 vừa qua, C&acirc;u lạc bộ Nhiếp ảnh Ban M&ecirc; v&agrave; Trại s&aacute;ng t&aacute;c &ldquo;Đại ng&agrave;n T&acirc;y Nguy&ecirc;n&rdquo; đ&atilde; tổ chức buổi giao lưu nh&acirc;n chuyến gh&eacute; thăm v&agrave; s&aacute;ng t&aacute;c tại Đắk Lắk. Trại s&aacute;ng t&aacute;c Nhiếp ảnh&nbsp;<em style=\"box-sizing: border-box;\">&ldquo;</em><em style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; border: 1pt none windowtext; padding: 0in; font-style: normal;\">Đại ng&agrave;n T&acirc;y Nguy&ecirc;n</span></em><em style=\"box-sizing: border-box;\">&rdquo;</em>&nbsp;l&agrave; một hoạt động nghệ thuật được tổ chức nhằm hưởng ứng Năm Du lịch Quốc gia 2014 tổ chức tại Tp Đ&agrave; Lạt - L&acirc;m Đồng do Bộ Văn h&oacute;a Thể Thao v&agrave; Du lịch tổ chức. Trại s&aacute;ng t&aacute;c quy tụ 15 nghệ sĩ nhiếp ảnh xuất sắc, danh tiếng tr&ecirc;n cả nước tham dự.<strong style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-weight: normal; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">Trong v&ograve;ng mười ng&agrave;y, 15 nghệ sỹ nhiếp ảnh của cả nước sẽ đi thực tế, ghi lại những h&igrave;nh ảnh đẹp nhất về T&acirc;y Nguy&ecirc;n,</span></strong><span class=\"apple-converted-space\" style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">&nbsp;</span></span><span style=\"box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">thể hiện v&agrave; quảng b&aacute; những khoảnh khắc đẹp nhất về văn h&oacute;a, đời sống, con người, phong cảnh n&uacute;i rừng h&ugrave;ng vỹ của v&ugrave;ng đất T&acirc;y Nguy&ecirc;n.&nbsp;</span>Tại buổi giao lưu, c&aacute;c th&agrave;nh vi&ecirc;n Trại s&aacute;ng t&aacute;c đ&atilde; c&oacute; dịp được thưởng thức những điệu h&aacute;t T&acirc;y Nguy&ecirc;n l&agrave;m say đắm l&ograve;ng người, lắng nghe giai điệu của những nhạc cụ d&acirc;n tộc truyền thống, hiểu biết th&ecirc;m những phong tục tập qu&aacute;n nơi đ&acirc;y qua men say rượu cần, qua những c&acirc;u chuyện th&acirc;n t&igrave;nh. Đồng thời, đ&acirc;y cũng l&agrave; dịp giao lưu, học hỏi, chia sẻ kinh nghiệm về nhiếp ảnh của c&aacute;c th&agrave;nh vi&ecirc;n CLB nhiếp ảnh Ban M&ecirc; với c&aacute;c th&agrave;nh vi&ecirc;n Trại s&aacute;ng t&aacute;c. Th&ocirc;ng qua c&aacute;c đợt giao lưu nhằm&nbsp;<span style=\"box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">n&acirc;ng cao chất lượng chụp ảnh nghệ thuật cho c&aacute;c hội vi&ecirc;n CLB&hellip;</span></span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 0.5in;\"><span style=\"box-sizing: border-box; font-size: larger;\">&nbsp;Bước đầu gia nhập CLB phải ai cũng c&oacute; kỹ thuật chụp ảnh, nhiều người chỉ mới tập t&agrave;nh, sau khi được c&aacute;c hội vi&ecirc;n c&oacute; kinh nghiệm trong c&acirc;u lạc bộ hướng dẫn cho những người mới, động vi&ecirc;n học hỏi lẫn nhau, như b&aacute;c Quang Sơn l&uacute;c đầu mới v&agrave;o chưa năm được kỷ thuật chụp ảnh, nhưng sau một v&agrave;i lần đi sang t&aacute;c với CLB được c&aacute;c hội vi&ecirc;n kh&aacute;c hướng dẫn, B&aacute;c đ&atilde; nắm được kỷ thuật cơ bản.. B&aacute;c Nguyễn Dự, một hội vi&ecirc;n cao tuổi của CLB t&acirc;m sự: T&ocirc;i l&agrave; một c&aacute;c bộ về hưu, đam m&ecirc; nhiếp ảnh đ&atilde; l&acirc;u, nhưng trước đ&acirc;y do bận c&ocirc;ng việc Nh&agrave; nước, th&ecirc;m v&agrave;o đ&oacute; l&agrave; phải chăm lo cho con c&aacute;i ăn học, nay đ&atilde; về hưu, con c&aacute;i đ&atilde; trưởng th&agrave;nh t&ocirc;i mới c&oacute; điều kiện thực hiện sở th&iacute;ch của m&igrave;nh v&agrave; đ&acirc;y l&agrave; s&acirc;n chơi để anh em ch&uacute;ng t&ocirc;i c&oacute; điều kiện học tập v&agrave; giao lưu lẫn nhau&hellip;</span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 0.5in;\"><span style=\"box-sizing: border-box; font-size: larger;\">C&aacute;c hội vi&ecirc;n trong CLB mỗi người một nghề kh&aacute;c nhau, người th&igrave; c&aacute;n bộ vi&ecirc;n chức, nh&agrave; b&aacute;o, người th&igrave; chụp h&igrave;nh đ&aacute;m cưới v&agrave; cũng c&oacute; hội vi&ecirc;n chụp h&igrave;nh dạo v&agrave; c&oacute; những sở trường kh&aacute;c nhau, như: Sở trường ảnh thời sự b&aacute;o ch&iacute;, ảnh ch&acirc;n dung, phong cảnh thi&ecirc;n nhi&ecirc;n&hellip; nhưng tất cả đều c&oacute; một điểm chung l&agrave; niềm đam m&ecirc; nhiếp ảnh. Cứ đến mỗi kỳ sinh hoạt c&aacute;c hội vi&ecirc;n lại tập trung giới thiệu những t&aacute;c phẩm mới s&aacute;ng t&aacute;c của m&igrave;nh, anh em g&oacute;p &yacute; để t&aacute;c phẩm chin chu hơn.&nbsp;&nbsp; Tại c&aacute;c đợt sinh hoạt, Ban chủ nhiệm th&ocirc;ng b&aacute;o cho hội vi&ecirc;n c&aacute;c cuộc thi ảnh của do Trung ương, khu vực v&agrave; tỉnh ph&aacute;t động v&agrave; đề ra kế hoạch s&aacute;ng t&aacute;c v&agrave; sinh hoạt kỳ sau&hellip;</span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 0.5in;\"><span style=\"box-sizing: border-box; font-size: larger;\">&Ocirc;ng L&ecirc; Xu&acirc;n Chiến, Chủ nhiệm CLB cho biết: Trong thời gian tới&nbsp;<span style=\"box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">Ban chủ nhiệm thường xuy&ecirc;n tăng cường tổ chức giao lưu, s&aacute;ng t&aacute;c, trao đổi kinh nghiệm... &nbsp;Đồng thời ch&uacute; trọng ph&aacute;t triển hội vi&ecirc;n mới; tạo t&igrave;nh đo&agrave;n kết gắn b&oacute; giữa c&aacute;c hội vi&ecirc;n trong CLB, hướng c&aacute;c hoạt động theo t&ocirc;n chỉ, mục đ&iacute;ch v&agrave; tu&acirc;n thủ c&aacute;c quy định của ph&aacute;p luật hiện h&agrave;nh, c&aacute;c quy định của tỉnh, của ngh&agrave;nh về lĩnh vực Văn h&oacute;a.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 0.5in;\"><span style=\"box-sizing: border-box; font-size: larger;\">Tuy mới chỉ l&agrave; kết quả bước đầu, nhưng&nbsp;<span style=\"box-sizing: border-box; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">CLB Nhiếp ảnh Ban M&ecirc; đang từng bước khẳng đinh m&igrave;nh, nhằm x&acirc;y dựng CLB ng&agrave;y c&agrave;ng vững mạnh, g&oacute;p phần đưa phong tr&agrave;o nghệ thuật nhiếp ảnh tỉnh nh&agrave; ng&agrave;y c&agrave;ng ph&aacute;t triển. Đồng thời, t</span>h&ocirc;ng qua h&igrave;nh ảnh g&oacute;p phần giới thiệu, quảng b&aacute; h&igrave;nh ảnh con người, văn h&oacute;a c&aacute;c d&acirc;n tộc T&acirc;y Nguy&ecirc;n đến với bạn b&egrave; trong v&agrave; ngo&agrave;i nước.</span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 0.5in;\"><strong style=\"box-sizing: border-box;\"><u style=\"box-sizing: border-box;\">Một số h&igrave;nh ảnh&nbsp;</u></strong></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center; text-indent: 0.5in;\"><strong style=\"box-sizing: border-box;\"><u style=\"box-sizing: border-box;\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%;\" src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/10255372_666938646733879_6174701917973173057_n.jpg\" alt=\"\" width=\"100%\" height=\"\" /></u></strong></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center; text-indent: 0.5in;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Ảnh: Ng&ocirc; Minh Phương</p>\r\n<p>&nbsp;</p>', 'posts/December2022/EPutcLmBal159nD2qlMu.jpg', 'cau-lac-bo-nhiep-anh-ban-me-noi-hoi-tu-nhung-niem-dam-me-nhiep-anh-nghe-thuat', NULL, NULL, 'PUBLISHED', 0, '2022-12-29 20:39:59', '2022-12-29 20:39:59'),
(58, 5, 23, 'Cấp chiêng và trang phục truyền thống cho một số buôn trong tỉnh', 'Cấp chiêng và trang phục truyền thống cho một số buôn trong tỉnh', NULL, '<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; line-height: 15pt; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; vertical-align: top;\"><span style=\"box-sizing: border-box; font-size: 10.5pt; color: #333333;\">Sở Văn h&oacute;a Thế thao v&agrave; Du lịch đang tiến h&agrave;nh cấp chi&ecirc;ng v&agrave; trang phục truyền thống cho c&aacute;c bu&ocirc;n đồng b&agrave;o d&acirc;n tộc thiểu số tại chỗ của 10 địa phương trong tỉnh.</span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center; line-height: 15pt; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; vertical-align: top;\" align=\"center\"><span style=\"box-sizing: border-box; font-size: 10.5pt; color: #333333;\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%;\" src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/_DSC3649.jpg\" alt=\"\" width=\"800\" height=\"499\" />&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 7.5pt; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center; line-height: 15pt; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><em style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 10.5pt; color: #a32402;\">C&aacute;c nghệ nh&acirc;n đang thẩm định chi&ecirc;ng trước khi cấp về cho c&aacute;c bu&ocirc;n.</span></em></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; line-height: 15pt; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; vertical-align: top;\"><span style=\"box-sizing: border-box; font-size: 10.5pt; color: #333333;\">C&aacute;c huyện &nbsp;được cấp chi&ecirc;ng v&agrave; trang phục truyền thống gồm: Ea Kar, Ea S&uacute;p, Ea H&rsquo;leo, M&rsquo; Đrắk, Lắk, Kr&ocirc;ng Năng, Kr&ocirc;ng Pắc, Kr&ocirc;ng Ana, Kr&ocirc;ng B&uacute;k v&agrave; Bu&ocirc;n Đ&ocirc;n (mỗi huyện một bộ chi&ecirc;ng). Trong đ&oacute; c&oacute; 7 bộ chi&ecirc;ng &Ecirc;đ&ecirc;, 2 bộ chi&ecirc;ng M&rsquo;N&ocirc;ng v&agrave; 1 bộ chi&ecirc;ng Gia Rai. Tại c&aacute;c bu&ocirc;n được cấp chi&ecirc;ng đợt n&agrave;y, Sở Văn h&oacute;a Thể thao c&ograve;n cấp trang phục truyền thống (gồm 47 &aacute;o nam&nbsp; truyền thống d&acirc;n tộc M&rsquo;N&ocirc;ng&nbsp; cho c&aacute;c th&agrave;nh vi&ecirc;n của&nbsp; một đội chi&ecirc;ng Bu&ocirc;n Đ&ocirc;n (x&atilde;&nbsp; Kr&ocirc;ng Na, huyện Bu&ocirc;n Đ&ocirc;n), 2 đội chi&ecirc;ng của bu&ocirc;n&nbsp; Pai Ar (x&atilde; Đắk Phơi, huyện Lắk) v&agrave; cấp 87 &aacute;o, v&aacute;y nữ truyền thống d&acirc;n tộc &Ecirc;đ&ecirc; cho c&aacute;c bu&ocirc;n, đội văn nghệ của c&aacute;c bu&ocirc;n thuộc 8 địa phương c&ograve;n lại.</span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; line-height: 15pt; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; vertical-align: top;\"><span style=\"box-sizing: border-box; font-size: 10.5pt; color: #333333;\">Việc cấp chi&ecirc;ng v&agrave; trang phục truyền thống được thực hiện theo Kế hoạch số 1336/KH &ndash;UBND, ng&agrave;y 28/2/2017 của UBND tỉnh về việc tổ chức triển khai thực hiện Nghị quyết số 05/2016/NQ-HĐND, ng&agrave;y 30-8-2016 của HĐND tỉnh về</span><span style=\"box-sizing: border-box; font-size: 10.5pt;\">&nbsp;bảo tồn, ph&aacute;t huy văn h&oacute;a cồng chi&ecirc;ng tỉnh Đắk Lawsk, giai đoạn 2016 &ndash; 2020.</span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: right;\"><span lang=\"EN-US\" style=\"box-sizing: border-box;\">HH</span></p>', 'posts/December2022/Q5vaC5SjBD6yAHHvwykw.jpg', 'cap-chieng-va-trang-phuc-truyen-thong-cho-mot-so-buon-trong-tinh', NULL, NULL, 'PUBLISHED', 0, '2022-12-29 21:00:39', '2022-12-29 21:00:39');
INSERT INTO `posts` (`id`, `author_id`, `category_id`, `title`, `seo_title`, `excerpt`, `body`, `image`, `slug`, `meta_description`, `meta_keywords`, `status`, `featured`, `created_at`, `updated_at`) VALUES
(59, 5, 25, 'Chương trình giới thiệu, quảng bá tác phẩm âm nhạc năm 2022', NULL, 'Tối ngày 30/11/2022, Trung tâm Văn hóa tỉnh tổ chức Chương trình giới thiệu, quảng bá tác phẩm âm nhạc năm 2022.', '<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><strong style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Tối ng&agrave;y 30/11</span></span></strong><strong style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">/2022</span></span></strong><strong style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">, Trung t&acirc;m Văn h&oacute;a tỉnh tổ chức Chương tr&igrave;nh giới thiệu, quảng b&aacute; t&aacute;c phẩm &acirc;m nhạc năm 2022.</span></span></strong></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Tại Chương tr&igrave;nh, những t&aacute;c phẩm &acirc;m nhạc đ&atilde; đạt giải thưởng trong tỉnh, c&aacute;c s&aacute;ng t&aacute;c mới trong giai đoạn hiện nay về Đắk Lắk như: &ldquo;C&agrave; ph&ecirc; h&aacute;t&rdquo; của NSƯT, Nhạc sĩ Y Ph&ocirc;n K&rsquo;Sơr, &ldquo;Vũ điệu Ei rei&rdquo; của t&aacute;c giả Nguyễn Cửu Long, &ldquo;Dạ kh&uacute;c y&ecirc;u&rdquo; của nhạc sĩ Trọng Ch&acirc;u; &ldquo;Giọt đắng kh&ocirc;ng m&agrave;u&rdquo; của t&aacute;c giả Nguyễn Văn L&yacute;&hellip;đ&atilde; được giới thiệu đến c&ocirc;ng ch&uacute;ng. Những ca kh&uacute;c với giai điệu, ca từ đẹp, dạt d&agrave;o cảm x&uacute;c của c&aacute;c nhạc sĩ, t&aacute;c giả trước vẻ đẹp của thi&ecirc;n nhi&ecirc;n, con người, ca ngợi t&igrave;nh y&ecirc;u qu&ecirc; hương, cuộc sống. Mỗi t&aacute;c phẩm c&ograve;n l&agrave; một g&oacute;c nh&igrave;n ri&ecirc;ng biệt của t&aacute;c giả về gi&aacute; trị văn h&oacute;a truyền thống của đồng b&agrave;o c&aacute;c d&acirc;n tộc thiểu số.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/gioi thieu quang ba tac pham am nhac (3)1.jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><em style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Tiết mục &ldquo;Dạ kh&uacute;c y&ecirc;u&rdquo; của Nhạc sĩ Trọng Ch&acirc;u do Diệu Thiện thể hiện</span></span></em></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/gioi thieu quang ba tac pham am nhac (8).jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><em style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">H&ograve;a tấu &ldquo;Vũ điệu Ei rei&rdquo; của t&aacute;c giả Nguyễn Cửu Long</span></span></em></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/gioi thieu quang ba tac pham am nhac (10).jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><em style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Tiết mục &ldquo;Nhớ đ&ecirc;m xoang&rdquo; của&nbsp; t&aacute;c giả Nguyễn Sĩ H&ugrave;ng do tam ca nữ&nbsp;thể hiện</span></span></em></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/gioi thieu quang ba tac pham am nhac (12).jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><em style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Tiết mục &ldquo;Vượt s&oacute;ng&rdquo; của t&aacute;c giả Nguyễn Văn L&yacute; do Kim Ngọc&nbsp;thể hiện</span></span></em></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Chương tr&igrave;nh c&ograve;n c&oacute; phần giao lưu với t&aacute;c giả Nguyễn Cửu Long v&agrave; Nguyễn Văn L&yacute;. Hai nghệ sĩ đ&atilde; chia sẻ với kh&aacute;n giả về niềm đam m&ecirc;, t&igrave;nh y&ecirc;u với &acirc;m nhạc, định hướng s&aacute;ng t&aacute;c trong thời gian tới.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/gioi thieu quang ba tac pham am nhac1.jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><em style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Phần giao lưu với t&aacute;c giả Nguyễn Cửu Long</span></span></em></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/gioi thieu quang ba tac pham.jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><em style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Phần giao lưu với t&aacute;c giả Nguyễn Văn L&yacute;</span></span></em></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Th&ocirc;ng qua chương tr&igrave;nh, c&aacute;c nghệ sỹ c&oacute; điều kiện gặp gỡ</span></span><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">,</span></span><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">&nbsp;trao đổi kinh nghiệm, học hỏi,&nbsp;</span></span><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">c&oacute;&nbsp;</span></span><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">th&ecirc;m động lực để tiếp tục t&igrave;m t&ograve;i, s&aacute;ng tạo ra nhiều t&aacute;c phẩm c&oacute; gi&aacute; trị tư tưởng, nghệ thuật. Đồng thời, đ&acirc;y c&ograve;n l&agrave; s&acirc;n chơi để&nbsp;</span></span><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">c&aacute;c nghệ sỹ&nbsp;</span></span><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">trải nghiệm hoạt động văn h&oacute;a, nghệ thuật, gặp gỡ, trực tiếp giao lưu&nbsp;</span></span><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">kh&aacute;n giả</span></span><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">; g&oacute;p phần cổ vũ, động vi&ecirc;n mọi tầng lớp Nh&acirc;n d&acirc;n t&iacute;ch cực học tập, lao động, sản xuất, x&acirc;y dựng qu&ecirc; hương Đắk Lắk ng&agrave;y c&agrave;ng gi&agrave;u đẹp.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/z3926040344634_1144feae89412c7180924a2b6e941270.jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><em style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Ban Tổ chức tặng hoa cho c&aacute;c nghệ sĩ</span></span></em></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Trước đ&oacute;, Trung t&acirc;m Văn h&oacute;a tỉnh đ&atilde; tổ chức 01 kỳ giới thiệu, quảng b&aacute; t&aacute;c phẩm &acirc;m nhạc, để những ca kh&uacute;c của c&aacute;c t&aacute;c giả đến gần hơn với c&ocirc;ng ch&uacute;ng./.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: right;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><strong style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Tin, ảnh: K&ocirc;ng Thảo</span></span></strong></span></span></p>', 'posts/December2022/4j43KEQhfGgqNUOiuxQe.jpg', 'chuong-trinh-gioi-thieu-quang-ba-tac-pham-am-nhac-nam-2022', 'Tối ngày 30/11/2022, Trung tâm Văn hóa tỉnh tổ chức Chương trình giới thiệu, quảng bá tác phẩm âm nhạc năm 2022.', 'Tối ngày 30/11/2022, Trung tâm Văn hóa tỉnh tổ chức Chương trình giới thiệu, quảng bá tác phẩm âm nhạc năm 2022.', 'PUBLISHED', 0, '2022-12-29 21:08:34', '2022-12-29 21:08:34'),
(60, 5, 25, 'Chương trình giới thiệu, quảng bá tác phẩm âm nhạc kỳ 1, tháng 7/2022', NULL, NULL, '<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\"><strong style=\"box-sizing: border-box;\">Tối ng&agrave;y 23/7, Trung t&acirc;m Văn h&oacute;a tỉnh tổ chức Chương tr&igrave;nh giới thiệu, quảng b&aacute; t&aacute;c phẩm &acirc;m nhạc kỳ 1, th&aacute;ng 7/2022.</strong></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Tại Chương tr&igrave;nh, những t&aacute;c phẩm &acirc;m nhạc đ&atilde; đạt giải thưởng trong tỉnh, c&aacute;c s&aacute;ng t&aacute;c mới trong giai đoạn hiện nay về Đắk Lắk như: &ldquo;Nồng n&agrave;n c&agrave; ph&ecirc;&rdquo; của NSƯT, Nhạc sĩ Quang Dũng; &ldquo;Cao nguy&ecirc;n m&ugrave;a xu&acirc;n về&rdquo; của Nhạc sĩ Trầm T&iacute;ch; &ldquo;S&oacute;ng c&oacute; thể n&agrave;o qu&ecirc;n&rdquo; (thơ B&iacute;ch Xoan), Nhạc sĩ Dương Tấn B&igrave;nh&hellip; đ&atilde; được giới thiệu đến c&ocirc;ng ch&uacute;ng. Đ&acirc;y l&agrave; những ca kh&uacute;c viết về cảm x&uacute;c của c&aacute;c nhạc sĩ trước vẻ đẹp của thi&ecirc;n nhi&ecirc;n, con người, ca ngợi t&igrave;nh y&ecirc;u qu&ecirc; hương, cuộc sống. Mỗi t&aacute;c phẩm c&ograve;n l&agrave; một g&oacute;c nh&igrave;n ri&ecirc;ng biệt của t&aacute;c giả về gi&aacute; trị văn h&oacute;a truyền thống của đồng b&agrave;o c&aacute;c d&acirc;n tộc thiểu số.</span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/gioi thieu tac pham am nhac 2022 (6).jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><em style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Ca sĩ Roda Mick biểu diễn ca kh&uacute;c \"Cao nguy&ecirc;n m&ugrave;a xu&acirc;n về\" của Nhạc sĩ Trầm T&iacute;ch</span></span></em></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/gioi thieu tac pham am nhac 2022 (9).jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><em style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Ca sĩ H\'Huyền Ml&ocirc; biểu diễn ca kh&uacute;c \"Lời tỏ t&igrave;nh đ&ecirc;m trăng\" của NSƯT, Nhạc sĩ Quang Dũng</span></span></em></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/gioi thieu tac pham am nhac 2022 (8).jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><em style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Ca sĩ Tường Vi biểu diễn ca kh&uacute;c \"S&oacute;ng c&oacute; thể n&agrave;o qu&ecirc;n\" của Nhạc sĩ Dương Tấn B&igrave;nh&nbsp;</span></span></em></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Chương tr&igrave;nh c&ograve;n c&oacute; phần giao lưu với NSƯT, Nhạc sĩ Quang Dũng v&agrave; Nhạc sĩ Dương Tấn B&igrave;nh. Hai nghệ sĩ đ&atilde; chia sẻ với kh&aacute;n giả về niềm đam m&ecirc;, t&igrave;nh y&ecirc;u với &acirc;m nhạc, định hướng s&aacute;ng t&aacute;c trong thời gian tới.</span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/gioi thieu tac pham am nhac 2022 (7).jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><em style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Phần giao lưu với 02 Nhạc sĩ&nbsp;</span></span></em></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/gioi thieu tac pham am nhac 2022 (3).jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Th&ocirc;ng qua chương tr&igrave;nh, c&aacute;c nghệ sĩ c&oacute; điều kiện gặp gỡ trao đổi kinh nghiệm, học hỏi, th&ecirc;m động lực để tiếp tục t&igrave;m t&ograve;i, s&aacute;ng tạo ra nhiều t&aacute;c phẩm c&oacute; gi&aacute; trị tư tưởng, nghệ thuật. Đồng thời, đ&acirc;y c&ograve;n l&agrave; s&acirc;n chơi để trải nghiệm hoạt động văn h&oacute;a, nghệ thuật, gặp gỡ, trực tiếp giao lưu với những nghệ sĩ; g&oacute;p phần cổ vũ, động vi&ecirc;n mọi tầng lớp Nh&acirc;n d&acirc;n t&iacute;ch cực học tập, lao động, sản xuất, x&acirc;y dựng qu&ecirc; hương Đắk Lắk ng&agrave;y c&agrave;ng gi&agrave;u đẹp.</span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/gioi thieu tac pham am nhac 2022 (1).jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><em style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Ban Tổ chức tặng hoa cho c&aacute;c nghệ sĩ</span></span></em></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/gioi thieu tac pham am nhac 2022 - Copy 1.jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><em style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Hội Văn học Nghệ thuật tỉnh tặng hoa cho c&aacute;c nghệ sĩ</span></span></em></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Trong năm 2022, Trung t&acirc;m Văn h&oacute;a tỉnh sẽ tổ chức 03 k&igrave; giới thiệu, quảng b&aacute; t&aacute;c phẩm &acirc;m nhạc để những ca kh&uacute;c&nbsp;của c&aacute;c t&aacute;c giả đến gần hơn với c&ocirc;ng ch&uacute;ng./.</span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: right;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\"><strong style=\"box-sizing: border-box;\">Tin, ảnh: K&ocirc;ng Thảo</strong></span></span></p>', 'posts/December2022/Z6RFq6KZblzqJTeV8aeQ.jpg', 'chuong-trinh-gioi-thieu-quang-ba-tac-pham-am-nhac-ky-1-thang-7-2022', NULL, NULL, 'PUBLISHED', 0, '2022-12-29 21:12:37', '2022-12-29 21:12:37'),
(61, 5, 25, 'Khai mạc Liên hoan Ca, Múa, Nhạc toàn quốc năm 2021 đợt 2', NULL, NULL, '<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\"><strong style=\"box-sizing: border-box;\">Tối 17/6, tại Trung t&acirc;m Văn h&oacute;a tỉnh Đắk Lắk, Cục Nghệ thuật Biểu diễn (Bộ Văn h&oacute;a, Thể thao v&agrave; Du lịch) phối hợp với UBND tỉnh Đắk Lắk, Hội Nhạc sĩ Việt Nam, Hội Nghệ sĩ M&uacute;a Việt Nam tổ chức khai mạc Li&ecirc;n hoan ca m&uacute;a nhạc to&agrave;n quốc năm 2021 đợt 2. Đến tham dự chương tr&igrave;nh c&oacute; b&agrave; H&rsquo;Yim Kđoh, Ph&oacute; Chủ tịch UBND tỉnh Đắk Lắk, Ph&oacute; Trưởng Ban chỉ đạo Li&ecirc;n hoan; Nghệ sĩ Ưu t&uacute; Trần Ly Ly, Quyền Cục trưởng Cục Nghệ thuật Biểu diễn, Trưởng Ban chỉ đạo Li&ecirc;n hoan; Thiếu tướng, Nhạc sĩ Nguyễn Đức Trịnh, Chủ tịch Hội Nhạc sĩ Việt Nam; NSND Phạm Anh Phương, Chủ tịch Hội Nghệ sĩ M&uacute;a Việt Nam; l&atilde;nh đạo c&aacute;c Sở Văn h&oacute;a, Thể thao v&agrave; Du lịch, Sở Văn h&oacute;a, Thể thao c&aacute;c tỉnh, th&agrave;nh phố c&oacute; đơn vị tham gia li&ecirc;n hoan; đ&ocirc;ng đảo kh&aacute;n giả y&ecirc;u nghệ thuật trong v&agrave; ngo&agrave;i tỉnh&hellip;</strong></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Li&ecirc;n hoan thu h&uacute;t sự tham gia của tr&ecirc;n 1.000 nghệ sĩ, diễn vi&ecirc;n đến từ 22 đơn vị nghệ thuật trong cả nước. Li&ecirc;n hoan được tổ chức diễn thi ở hai loại h&igrave;nh nghệ thuật: Loại h&igrave;nh ca, m&uacute;a, nhạc v&agrave; loại h&igrave;nh giao hưởng, nhạc vũ kịch, thanh xướng kịch. Mỗi đơn vị đăng k&yacute; tham gia 1 chương tr&igrave;nh c&oacute; thời lượng từ 65 - 110 ph&uacute;t, trường hợp đơn vị c&ocirc;ng lập c&oacute; nhiều đo&agrave;n, số lượng chương tr&igrave;nh c&oacute; thể tham gia tương ứng với số đo&agrave;n.</span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Ph&aacute;t biểu khai mạc Li&ecirc;n hoan, Nghệ sĩ Ưu t&uacute; Trần Ly Ly, Quyền Cục trưởng Cục Nghệ thuật Biểu diễn, Trưởng ban Tổ chức Li&ecirc;n hoan nhấn mạnh, Ban Chỉ đạo, Ban Tổ chức đ&aacute;nh gi&aacute; cao sự chuyển m&igrave;nh t&iacute;ch cực của c&aacute;c đơn vị nghệ thuật trong những năm qua để bắt kịp với sự thay đổi lớn lao đang diễn ra tr&ecirc;n mọi phương diện của đất nước. Li&ecirc;n hoan l&agrave; cơ hội để c&aacute;c nghệ sĩ hoạt động ở lĩnh vực nghệ thuật ca, m&uacute;a, nhạc được giao lưu, trao đổi&nbsp;kinh nghiệm, n&acirc;ng cao tr&igrave;nh độ nghề nghiệp; đồng thời l&agrave; dịp để cơ quan quản l&yacute; văn h&oacute;a v&agrave; l&atilde;nh đạo c&aacute;c đơn vị nghệ thuật ph&aacute;t hiện những t&agrave;i năng trẻ, từ đ&oacute; c&oacute; kế hoạch bồi dưỡng, ph&aacute;t triển ph&ugrave; hợp.</span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/gioi thieu tac pham am nhac 2022 - Copy 11.jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\"><em style=\"box-sizing: border-box;\">Nghệ sĩ Ưu t&uacute; Trần Ly Ly, Quyền Cục trưởng Cục Nghệ thuật Biểu diễn, Trưởng ban Tổ chức Li&ecirc;n hoan</em><em style=\"box-sizing: border-box;\">&nbsp;ph&aacute;t biểu khai mạc</em></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">L&agrave; đơn vị chủ nh&agrave; đăng cai tổ chức Li&ecirc;n hoan, b&agrave; H&rsquo;Yim Kđoh, Ph&oacute; Chủ tịch UBND tỉnh Đắk Lắk,&nbsp;Ph&oacute; Trưởng Ban chỉ đạo Li&ecirc;n hoan cho biết, Đắk Lắk l&agrave; v&ugrave;ng đất c&oacute; nền văn h&oacute;a đa dạng của 49 d&acirc;n tộc anh em, được g&igrave;n giữ qua nhiều thế hệ. Nhiều t&aacute;c phẩm ra đời tr&ecirc;n cao nguy&ecirc;n Đắk Lắk đ&atilde; l&agrave;m n&ecirc;n t&ecirc;n tuổi của nhiều nhạc sĩ, nghệ sĩ. Tỉnh Đắk Lắk nồng nhiệt ch&agrave;o đ&oacute;n nghệ sĩ, diễn vi&ecirc;n, c&aacute;n bộ của 22 Đo&agrave;n nghệ thuật về tham dự chương tr&igrave;nh. Li&ecirc;n hoan l&agrave; sự kiện c&oacute; &yacute; nghĩa nhằm bảo tồn, quảng b&aacute;, ph&aacute;t huy gi&aacute; trị nghệ thuật biểu diễn d&acirc;n gian, d&acirc;n tộc truyền thống đặc sắc của Việt Nam. Từng chương tr&igrave;nh của c&aacute;c đo&agrave;n nghệ thuật hứa hẹn mang đến những cảm x&uacute;c thăng hoa, m&atilde;n nh&atilde;n cho kh&aacute;n giả; cũng l&agrave; dịp để tỉnh Đắk Lắk giới thiệu, quảng b&aacute; về mảnh đất, con người, văn h&oacute;a v&agrave; tiềm năng du lịch của tỉnh đến với c&aacute;c tỉnh bạn v&agrave; du kh&aacute;ch.</span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/lien hoan ca mua nhac (6).jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\"><em style=\"box-sizing: border-box;\">B&agrave; H&rsquo;Yim Kđoh, Ph&oacute; Chủ tịch UBND tỉnh Đắk Lắk, Ph&oacute; Trưởng Ban chỉ đạo Li&ecirc;n hoan ph&aacute;t biểu</em></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/lien hoan ca mua nhac (5).jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\"><em style=\"box-sizing: border-box;\">UBND tỉnh&nbsp;Đắk Lắk tặng&nbsp;qu&agrave; lưu niệm cho Cục Nghệ thuật biểu diễn</em></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/lien hoan ca mua nhac (3).jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\"><em style=\"box-sizing: border-box;\">Tặng&nbsp;hoa, qu&agrave; lưu niệm cho 22 đo&agrave;n nghệ thuật tham gia Li&ecirc;n hoan</em></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Ngay sau Lễ khai mạc, Đo&agrave;n Ca m&uacute;a D&acirc;n tộc tỉnh Đắk Lắk l&agrave; đơn vị đầu ti&ecirc;n tham gia Li&ecirc;n hoan với&nbsp;11 tiết mục đặc sắc, chủ đề \"Những bức tranh Bazan đỏ\". Sự chuẩn bị c&ocirc;ng phu, đa dạng về thể loại,&nbsp;sự biểu diễn chuy&ecirc;n nghiệp của c&aacute;c nghệ sĩ, diễn vi&ecirc;n c&ugrave;ng tiếng cồng, tiếng chi&ecirc;ng, nhạc cụ d&acirc;n tộc&hellip; đ&atilde; mang lại những ph&uacute;t gi&acirc;y &acirc;m nhạc thăng hoa, đầy cảm x&uacute;c, để lại những ấn tượng s&acirc;u sắc trong l&ograve;ng kh&aacute;n giả.</span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/lien hoan ca mua nhac (2).jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\"><em style=\"box-sizing: border-box;\">Tiết mục trong chương tr&igrave;nh dự thi \"Những bức tranh Bazan đỏ\" của Đo&agrave;n Ca m&uacute;a D&acirc;n tộc tỉnh Đắk Lắk</em></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Li&ecirc;n hoan sẽ diễn ra đến ng&agrave;y 30/6./.</span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: right;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\"><strong style=\"box-sizing: border-box;\">Tin, ảnh: Phương Li&ecirc;n</strong></span></span></p>', 'posts/December2022/kFxdRQb6HZr8m0rSLqoh.jpg', 'khai-mac-lien-hoan-ca-mua-nhac-toan-quoc-nam-2021-dot-2', NULL, NULL, 'PUBLISHED', 0, '2022-12-29 21:16:54', '2022-12-29 21:16:54'),
(62, 5, 30, 'Biểu diễn chương trình Múa rối tổng hợp nhân ngày Quốc tế Thiếu nhi 1/6', NULL, NULL, '<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 12pt;\"><span style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', serif;\"><strong style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\">Ng&agrave;y&nbsp; 01/6/2022, Trung t&acirc;m Văn ho&aacute; tỉnh biểu diễn chương tr&igrave;nh M&uacute;a rối tổng hợp nh&acirc;n dịp ng&agrave;y Quốc tế Thiếu nhi 1/6&nbsp;phục vụ c&aacute;c ch&aacute;u tại Trường Mầm non Hoa Sen v&agrave; Trung t&acirc;m Đ&agrave;o tạo, Huấn luyện v&agrave; Thi đấu TDTT tỉnh Đắk Lắk.</span></strong></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 12pt;\"><span style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', serif;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box;\">Chương tr&igrave;nh gồm c&aacute;c tiết mục rối l&ugrave;n, rối que như: Ch&agrave;o m&ugrave;a h&egrave;, B&eacute; M&egrave;o lười, M&ugrave;a h&egrave; đ&aacute;ng nhớ, Ban nhạc Heo con&hellip; được d&agrave;n dựng sinh động, nội dung&nbsp;</span></span></span><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; color: black;\">phong ph&uacute;, hấp dẫn c&aacute;c ch&aacute;u thiếu nhi</span></span></span><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box;\">. Đặc biệt, chương tr&igrave;nh c&ograve;n lồng gh&eacute;p c&aacute;c tiết mục nhảy hiện đại do C&acirc;u lạc bộ BMT Kisd thuộc Trung t&acirc;m Văn h&oacute;a tỉnh biểu diễn như: Babyshak Remix hiphop, Mashup HYLT- BangBangBang&hellip;</span></span></span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 12pt;\"><span style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', serif;\"><span style=\"box-sizing: border-box; font-size: 14pt;\">C&aacute;c tiết mục biểu diễn đ&atilde; mang đến cho c&aacute;c ch&aacute;u thiếu nhi những tiếng cười sảng kho&aacute;i, vui vẻ, &yacute; nghĩa v&agrave; thu h&uacute;t được đ&ocirc;ng đảo kh&aacute;n giả đến xem v&agrave; cổ vũ.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 12pt;\"><span style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', serif;\"><em style=\"box-sizing: border-box;\"><strong style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; color: black;\">Một số h&igrave;nh ảnh biểu diễn:</span></span></span></strong></em></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/mua roi (2)1.jpg\" alt=\"\" width=\"80%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/mua roi (4).jpg\" alt=\"\" width=\"80%\" />&nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><span style=\"box-sizing: border-box; font-size: 12pt;\"><span style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', serif;\"><em style=\"box-sizing: border-box;\"><strong style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; color: black;\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%; height: 402px; width: 800px;\" src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/mua roi (1).jpg\" alt=\"\" /></span></span></span></strong></em></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\">&nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\">&nbsp;</p>', 'posts/December2022/r1hutsRzfF8LqRaViXj5.jpg', 'bieu-dien-chuong-trinh-mua-roi-tong-hop-nhan-ngay-quoc-te-thieu-nhi-1-6', NULL, NULL, 'PUBLISHED', 0, '2022-12-29 21:22:28', '2022-12-29 21:22:28'),
(63, 5, 30, 'Biểu diễn chương trình múa rối tổng hợp nhân ngày Quốc tế Thiếu nhi 1/6 năm 2020', NULL, 'Từ ngày 29/5 đến 01/6/2020, Đội Tuyên truyền lưu động thuộc Trung tâm Văn hoá tỉnh tổ chức biểu diễn nhân dịp ngày Quốc tế Thiếu nhi 1/6 tại các Trường Mầm non trong thành phố Buôn Ma Thuột.', '<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Arial, sans-serif;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\"><span style=\"box-sizing: border-box;\">Từ ng&agrave;y&nbsp;</span></span></span><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\"><span style=\"box-sizing: border-box;\">2</span></span></span><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\"><span style=\"box-sizing: border-box;\">9/5 đến 01/6/2020, Đội&nbsp;</span></span></span><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\"><span style=\"box-sizing: border-box;\">Tuy&ecirc;n truyền lưu động</span></span></span><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\"><span style=\"box-sizing: border-box;\">&nbsp;thuộc Trung t&acirc;m Văn ho&aacute; tỉnh tổ chức&nbsp;</span></span></span><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\"><span style=\"box-sizing: border-box;\">biểu diễn nh&acirc;n dịp ng&agrave;y Quốc tế Thiếu nhi 1/6</span></span></span><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\"><span style=\"box-sizing: border-box;\">&nbsp;tại c&aacute;c Trường Mầm non trong th&agrave;nh phố Bu&ocirc;n Ma Thuột.</span></span></span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Arial, sans-serif;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Chương tr&igrave;nh gồm c&aacute;c tiết mục rối l&ugrave;n như: Ch&agrave;o h&egrave;, Ch&uacute;ng em bảo vệ m&ocirc;i trường, N&agrave;ng Bạch Tuyết v&agrave; bảy ch&uacute; l&ugrave;n, C&ocirc; Đ&ocirc;i thượng ng&agrave;n v&agrave; vở rối &ldquo;M&ugrave;a h&egrave; đ&aacute;ng nhớ&rdquo; đ&atilde; được anh chị em trong Đội thể hiện rất vui nhộn, hấp dẫn. Những điểm đội đến biểu diễn thực sự đ&atilde; mang đến cho c&aacute;c ch&aacute;u thiếu nhi niềm vui, sự h&aacute;o hức về ng&agrave;y Quốc tế Thiếu nhi đ&aacute;ng nhớ.</span></span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Arial, sans-serif;\"><em style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Một số h&igrave;nh ảnh biểu diễn:</span></span></em></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><br style=\"box-sizing: border-box;\" /><br /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/HINH 1.jpg\" alt=\"\" width=\"80%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/KHAN GIA.jpg\" alt=\"\" width=\"80%\" /></p>', 'posts/December2022/oBtI8Fnw6CgcCgPhf77X.jpg', 'bieu-dien-chuong-trinh-mua-roi-tong-hop-nhan-ngay-quoc-te-thieu-nhi-1-6-nam-2020', 'Từ ngày 29/5 đến 01/6/2020, Đội Tuyên truyền lưu động thuộc Trung tâm Văn hoá tỉnh tổ chức biểu diễn nhân dịp ngày Quốc tế Thiếu nhi 1/6 tại các Trường Mầm non trong thành phố Buôn Ma Thuột.', 'Từ ngày 29/5 đến 01/6/2020, Đội Tuyên truyền lưu động thuộc Trung tâm Văn hoá tỉnh tổ chức biểu diễn nhân dịp ngày Quốc tế Thiếu nhi 1/6 tại các Trường Mầm non trong thành phố Buôn Ma Thuột.', 'PUBLISHED', 0, '2022-12-29 21:24:26', '2022-12-29 21:24:26');
INSERT INTO `posts` (`id`, `author_id`, `category_id`, `title`, `seo_title`, `excerpt`, `body`, `image`, `slug`, `meta_description`, `meta_keywords`, `status`, `featured`, `created_at`, `updated_at`) VALUES
(64, 5, 30, 'Đội Nghệ thuật múa rối diễn chương trình tết Trung thu 2019', NULL, 'Nhằm phục vụ các cháu thiếu niên, nhi đồng Tết Trung thu 2019, Đội nghệ thuật Múa rối (Trung tâm Văn hóa tỉnh) tổ chức Chương trình kịch rối tổng hợp phục vụ các cháu thiếu nhi vùng sâu, vùng xa, vùng đồng bào dân tộc thiểu số tại một số huyện Krông Bông, Krông Pắc và một số ban, ngành, đơn vị trường học trên địa bàn Thành phố Buôn Ma Thuột', '<p style=\"box-sizing: border-box; margin: 0cm 0cm 0.0001pt; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 36pt; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"box-sizing: border-box; font-size: larger;\"><span style=\"box-sizing: border-box; font-family: Arial, sans-serif; color: black;\">Nhằm phục vụ c&aacute;c ch&aacute;u thiếu ni&ecirc;n, nhi đồng Tết Trung thu 2019, Đội nghệ thuật M&uacute;a rối (Trung t&acirc;m Văn h&oacute;a tỉnh) tổ chức Chương tr&igrave;nh kịch rối tổng hợp phục vụ c&aacute;c ch&aacute;u thiếu nhi v&ugrave;ng s&acirc;u, v&ugrave;ng xa, v&ugrave;ng đồng b&agrave;o d&acirc;n tộc thiểu số tại một số huyện Kr&ocirc;ng B&ocirc;ng</span><span lang=\"EN-US\" style=\"box-sizing: border-box; font-family: Arial, sans-serif; color: black;\">, Kr&ocirc;ng</span><span lang=\"EN-US\" style=\"box-sizing: border-box; font-family: Arial, sans-serif; color: black;\">&nbsp;</span><span lang=\"EN-US\" style=\"box-sizing: border-box; font-family: Arial, sans-serif; color: black;\">Pắc&nbsp;</span><span style=\"box-sizing: border-box; font-family: Arial, sans-serif; color: black;\">v&agrave; một số ban, ng&agrave;nh, đơn vị trường học tr&ecirc;n địa b&agrave;n Th&agrave;nh phố Bu&ocirc;n Ma Thuột</span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0cm 0cm 0.0001pt; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 36pt; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"box-sizing: border-box; font-size: larger;\"><span style=\"box-sizing: border-box; font-family: Arial, sans-serif; color: black;\">Chương tr&igrave;nh được thể hiện qua c&aacute;c vở kịch rối, như: M&uacute;a l&acirc;n, M&uacute;a rối đố em, C&acirc;t củ cải thần kỳ, Rối gi&atilde; gạo ch&agrave;y tay, D&ecirc; đen dũng cảm, Vũ điệu ng&agrave;y h&egrave;, Quả dưa ti&ecirc;n, Tấm c&aacute;m&hellip; được d&agrave;n dựng kh&aacute; phong ph&uacute; về nội dung, đa dạng h&igrave;nh thức, mang đậm bản sắc văn h&oacute;a c&aacute;c d&acirc;n tộc T&acirc;y Nguy&ecirc;n, đ&atilde; để lại nhiều ấn tượng tốt đẹp trong l&ograve;ng c&aacute;c ch&aacute;u thiếu nhi. Đ&acirc;y l&agrave; s&acirc;n chơi bổ &iacute;ch, nhằm động vi&ecirc;n c&aacute;c ch&aacute;u chăm chỉ học tập, v&acirc;ng lời thầy c&ocirc;, cha mẹ&hellip; Chương tr&igrave;nh đ&atilde; thu h&uacute;t được đ&ocirc;ng đảo c&aacute;c ch&aacute;u thiếu nhi v&agrave; c&aacute;c bậc phụ huynh đến xem v&agrave; cổ vũ.</span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0cm 0cm 0.0001pt; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 36pt; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><em style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: larger;\">Một số h&igrave;nh ảnh biểu diễn của Đội:</span></em></p>\r\n<p style=\"box-sizing: border-box; margin: 0cm 0cm 0.0001pt; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 36pt; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">&nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin: 0cm 0cm 0.0001pt; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 36pt; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/IMG_5217.jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0cm 0cm 0.0001pt; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 36pt; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">&nbsp;<img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/IMG_5219 (2).jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0cm 0cm 0.0001pt; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 36pt; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">&nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin: 0cm 0cm 0.0001pt; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 36pt; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">&nbsp;<img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/IMG_5216.jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0cm 0cm 0.0001pt; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify; text-indent: 36pt; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">&nbsp;</p>', 'posts/December2022/m7r5AVd99LzHWnkEpOoe.jpg', 'doi-nghe-thuat-mua-roi-dien-chuong-trinh-tet-trung-thu-2019', 'Nhằm phục vụ các cháu thiếu niên, nhi đồng Tết Trung thu 2019, Đội nghệ thuật Múa rối (Trung tâm Văn hóa tỉnh) tổ chức Chương trình kịch rối tổng hợp phục vụ các cháu thiếu nhi vùng sâu, vùng xa, vùng đồng bào dân tộc thiểu số tại một số huyện Krông Bông, Krông Pắc và một số ban, ngành, đơn vị trường học trên địa bàn Thành phố Buôn Ma Thuột', 'Nhằm phục vụ các cháu thiếu niên, nhi đồng Tết Trung thu 2019, Đội nghệ thuật Múa rối (Trung tâm Văn hóa tỉnh) tổ chức Chương trình kịch rối tổng hợp phục vụ các cháu thiếu nhi vùng sâu, vùng xa, vùng đồng bào dân tộc thiểu số tại một số huyện Krông Bông, Krông Pắc và một số ban, ngành, đơn vị trường học trên địa bàn Thành phố Buôn Ma Thuột', 'PUBLISHED', 0, '2022-12-29 23:57:58', '2022-12-29 23:57:58'),
(66, 5, 32, 'Chương trình Vui tết Trung thu năm 2022', NULL, 'Tối ngày 8/9 (nhằm ngày 13/8 âm lịch), Trung tâm Văn hóa tỉnh tổ chức Chương trình Vui tết Trung thu năm 2022 cho con em của viên chức, người lao động và các cháu thiếu nhi tham gia sinh hoạt, học tập tại đơn vị.', '<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><strong style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Tối ng&agrave;y 8/9 (nhằm ng&agrave;y 13/8 &acirc;m lịch), Trung t&acirc;m Văn h&oacute;a tỉnh tổ chức Chương tr&igrave;nh Vui tết Trung thu năm 2022 cho con em của vi&ecirc;n chức, người lao động v&agrave; c&aacute;c ch&aacute;u thiếu nhi tham gia sinh hoạt, học tập tại đơn vị.</span></span></strong></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Qua Chương tr&igrave;nh, c&aacute;c ch&aacute;u thiếu nhi&nbsp;<span style=\"box-sizing: border-box; color: black;\">được</span>&nbsp;thưởng thức nhiều tiết mục M&uacute;a rối l&ugrave;n, rối que như: B&eacute; M&egrave;o lười, Ban nhạc Heo con, Tấm c&aacute;m&hellip; được d&agrave;n dựng sinh động, nội dung phong ph&uacute;, hấp dẫn. Ngo&agrave;i ra, c&aacute;c ch&aacute;u c&ograve;n xem m&uacute;a L&acirc;n - Rồng do CLB V&otilde; thuật L&acirc;n sư rồng Nh&acirc;n Nghĩa Đường biểu diễn; tham gia c&aacute;c tr&ograve; chơi vui nhộn v&agrave; giao lưu văn nghệ s&ocirc;i nổi, th&uacute; vị với chị Hằng, ch&uacute; Cuội.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">C&aacute;c tiết mục biểu diễn đ&atilde; mang đến cho c&aacute;c ch&aacute;u thiếu nhi những tiếng cười sảng kho&aacute;i, vui vẻ, &yacute; nghĩa v&agrave; thu h&uacute;t được đ&ocirc;ng đảo kh&aacute;n giả đến xem v&agrave; cổ vũ. Đ&acirc;y l&agrave; hoạt động thường ni&ecirc;n của Trung t&acirc;m Văn h&oacute;a tỉnh, nhằm tạo cơ hội để c&aacute;c ch&aacute;u vui chơi, hiểu &yacute; nghĩa v&agrave; c&oacute; ng&agrave;y tết Trung thu rộn r&agrave;ng, đong đầy y&ecirc;u thương./.</span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><strong style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Một số h&igrave;nh ảnh của Chương tr&igrave;nh:</span></span></span></span></strong></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/vui tet trung thu (1).jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/vui tet trung thu (3).jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/vui tet trung thu (4).jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/vui tet trung thu (5).jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/vui tet trung thu (6).jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: right;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box; font-family: Calibri, \'sans-serif\';\"><strong style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Tin, ảnh: Thanh Ng&acirc;n</span></span></strong></span></span></p>', 'posts/December2022/O3TXmdOT6nareX6gHyX0.jpg', 'chuong-trinh-vui-tet-trung-thu-nam-2022', 'Tối ngày 8/9 (nhằm ngày 13/8 âm lịch), Trung tâm Văn hóa tỉnh tổ chức Chương trình Vui tết Trung thu năm 2022 cho con em của viên chức, người lao động và các cháu thiếu nhi tham gia sinh hoạt, học tập tại đơn vị.', 'Tối ngày 8/9 (nhằm ngày 13/8 âm lịch), Trung tâm Văn hóa tỉnh tổ chức Chương trình Vui tết Trung thu năm 2022 cho con em của viên chức, người lao động và các cháu thiếu nhi tham gia sinh hoạt, học tập tại đơn vị.', 'PUBLISHED', 0, '2022-12-30 00:03:05', '2022-12-30 00:03:05'),
(68, 5, 32, 'TRANG CHỦ  CÔNG ĐOÀN Trung tâm Văn hóa tỉnh tổ chức Hội nghị Viên chức năm 2021', NULL, 'Ngày 25/12/2020 Trung tâm Văn hóa tỉnh đã tổ chức Hội nghị Viên chức năm 2021, nhằm đánh giá kết quả thực hiện nhiệm vụ năm 2020 và đề ra phương hướng, biện pháp thực hiện nhiệm vụ năm 2021.', '<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Calibri, sans-serif;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Ng&agrave;y 25/12/2020 Trung t&acirc;m Văn h&oacute;a tỉnh đ&atilde; tổ chức Hội nghị Vi&ecirc;n chức năm 2021,<strong style=\"box-sizing: border-box;\">&nbsp;</strong></span></span><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\"><span style=\"box-sizing: border-box; color: black;\">nhằm đ&aacute;nh gi&aacute;&nbsp;kết quả thực hiện nhiệm vụ năm 2020 v&agrave; đề ra phương hướng, biện ph&aacute;p thực hiện nhiệm vụ năm 2021.</span></span></span></span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Calibri, sans-serif;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Tham dự Hội nghị c&oacute; đồng ch&iacute; Đặng Gia Duẩn, Ph&oacute; B&iacute; thư Đảng ủy, Ph&oacute; Gi&aacute;m đốc Sở Văn h&oacute;a, Thể thao v&agrave; Du lịch, c&aacute;c đồng ch&iacute; l&atilde;nh đạo Ph&ograve;ng, đơn vị trực thuộc Sở c&ugrave;ng to&agrave;n thể vi&ecirc;n chức, người lao động của Trung t&acirc;m Văn h&oacute;a tỉnh.</span></span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; max-width: 100%;\" src=\"http://trungtamvanhoadaklak.gov.vn/CMS/Content/trung1.jpg\" alt=\"\" /><img src=\"https://tinhtoan.kennatech.vn/public/storage/posts/December2022/trung1.jpg\" alt=\"\" width=\"100%\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: center;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Calibri, sans-serif;\"><em style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-size: 12pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">C&aacute;c Đại biểu tham dự Hội nghị</span></span></em></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 11pt;\"><span style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Calibri, sans-serif;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Hội nghị đ&atilde; th&ocirc;ng qua c&aacute;c b&aacute;o c&aacute;o: B&aacute;o c&aacute;o tổng kết hoạt động c&ocirc;ng t&aacute;c hoạt động năm 2020 v&agrave; phương hướng nhiệm vụ năm 2021. B&aacute;o c&aacute;o tổng kết hoạt động Ban Thanh tra Nh&acirc;n d&acirc;n&nbsp;năm 2020 phương hướng hoạt động năm 2021. Theo đ&oacute;, năm 2020, Tập thể l&atilde;nh đạo Trung t&acirc;m Văn ho&aacute; tỉnh đ&atilde; thực tốt c&ocirc;ng t&aacute;c gi&aacute;o dục ch&iacute;nh trị tư tưởng, qu&aacute;n triệt, phổ biến chủ trương, đường lối của Đảng, ch&iacute;nh s&aacute;ch, ph&aacute;p luật của Nh&agrave; nước đến từng vi&ecirc;n chức, người lao động trong đơn vị. Tiếp tục đẩy mạnh việc &ldquo;Học tập v&agrave; l&agrave;m theo tư tưởng, đạo đức, phong c&aacute;ch Hồ Ch&iacute; Minh\". 100% vi&ecirc;n chức, người lao động c&oacute; &yacute; thức trong việc ph&ograve;ng, chống tham nhũng, thực h&agrave;nh tiết kiệm, chống l&atilde;ng ph&iacute;. B&aacute;m s&aacute;t chương tr&igrave;nh, kế hoạch c&ocirc;ng t&aacute;c năm 2020, đơn vị đ&atilde; chủ động phối hợp với c&aacute;c cơ quan đơn vị tổ chức tốt c&aacute;c hoạt động văn h&oacute;a văn nghệ ch&agrave;o mừng c&aacute;c ng&agrave;y lễ lớn, phục vụ nhiệm vụ ch&iacute;nh trị, tuy&ecirc;n truyền của tỉnh, của ng&agrave;nh.</span></span></span></span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #222222; font-family: \'Roboto Condensed\', sans-serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-size: 14pt;\"><span style=\"box-sizing: border-box; font-family: \'Times New Roman\', \'serif\';\">Tại Hội nghị, đại diện cho c&aacute;c ph&ograve;ng chuy&ecirc;n m&ocirc;n, đội đ&atilde; c&oacute; &yacute; kiến tham luận về những kh&oacute; khăn trong qu&aacute; tr&igrave;nh c&ocirc;ng t&aacute;c, đề xuất được trang bị cơ sở vật chất để phục vụ c&ocirc;ng việc ng&agrave;y tốt hơn. Thay mặt đo&agrave;n chủ tịch đồng ch&iacute; L&ecirc; Kim Quang &ndash; Ph&oacute; Gi&aacute;m đốc phụ tr&aacute;ch Trung t&acirc;m Văn h&oacute;a tỉnh đ&atilde; tiếp thu v&agrave; giải đ&aacute;p c&aacute;c &yacute; kiến, đề xuất của&nbsp;vi&ecirc;n chức, người lao động. Với sự thống nhất cao,&nbsp;100% vi&ecirc;n chức, người lao động tham dự Hội nghị&nbsp;đ&atilde;&nbsp;biểu quyết nhất&nbsp;tr&iacute; với&nbsp;nhữ